import { Test, TestingModule } from '@nestjs/testing';
import { AppController } from './app.controller';
import { AppService } from './app.service';

describe('AppController', () => {
  let appController: AppController;

  beforeEach(async () => {
    const app: TestingModule = await Test.createTestingModule({
      controllers: [AppController],
      providers: [AppService],
    }).compile();

    appController = app.get<AppController>(AppController);
  });

  describe('root', () => {
    it('should return "Hello World!"', () => {
      expect(appController.getHello()).toBe('Hello World!');
    });
  });
});

import { createParamDecorator, ExecutionContext } from '@nestjs/common';

export const CurrentUser = createParamDecorator(
  (data: unknown, ctx: ExecutionContext) => {
    const request = ctx.switchToHttp().getRequest();
    return request.user;
  },
);

import {
  ArgumentsHost,
  Catch,
  ExceptionFilter,
  HttpException,
  HttpStatus,
} from '@nestjs/common';
import { Request, Response } from 'express';
import { PrismaService } from '../../prisma/prisma.service';

@Catch()
export class GlobalExceptionFilter implements ExceptionFilter {
  constructor(private readonly prisma: PrismaService) {}

  private sanitize(obj: any): any {
    if (!obj || typeof obj !== 'object') return obj;
    const sensitiveKeys = new Set([
      'password',
      'newPassword',
      'currentPassword',
      'token',
    ]);
    if (Array.isArray(obj)) {
      return obj.map((item) => this.sanitize(item));
    }
    const clone: Record<string, any> = {};
    for (const [key, value] of Object.entries(obj)) {
      if (sensitiveKeys.has(key)) continue;
      clone[key] = typeof value === 'object' ? this.sanitize(value) : value;
    }
    return clone;
  }

  async catch(exception: any, host: ArgumentsHost) {
    const ctx = host.switchToHttp();
    const response = ctx.getResponse<Response>();
    const request = ctx.getRequest<Request>();

    const status =
      exception instanceof HttpException
        ? exception.getStatus()
        : HttpStatus.INTERNAL_SERVER_ERROR;

    const message =
      exception instanceof HttpException
        ? (exception.getResponse() as any)?.message ||
          exception.message ||
          'Error'
        : exception?.message || 'Internal server error';

    const stack = exception?.stack;

    // Attempt to persist error log (ignore failures to avoid masking original error)
    try {
      const prismaAny = this.prisma as any;
      if (prismaAny?.errorLog?.create) {
        await prismaAny.errorLog.create({
          data: {
            level: status >= 500 ? 'ERROR' : 'WARN',
            message: Array.isArray(message)
              ? message.join(', ')
              : String(message),
            stack,
            statusCode: status,
            method: request.method,
            path: request.url,
            userId: (request as any)?.user?.id ?? null,
            context: {
              ip: request.ip,
              userAgent: request.headers['user-agent'],
              params: this.sanitize(request.params),
              query: this.sanitize(request.query),
              body: this.sanitize(request.body),
            },
          },
        });
      }
    } catch (e) {
      console.error('Failed to persist ErrorLog:', e?.message || e);
    }

    const payload: any = {
      statusCode: status,
      message: Array.isArray(message) ? message[0] : message,
      timestamp: new Date().toISOString(),
      path: request.url,
    };

    response.status(status).json(payload);
  }
}

import 'reflect-metadata';
// Preload iconv-lite encodings to avoid rare "Cannot find module '../encodings'" errors
// that can occur when raw-body/body-parser triggers a lazy require in certain environments.
import 'iconv-lite/encodings';
import { NestFactory } from '@nestjs/core';
import { ValidationPipe } from '@nestjs/common';
import { AppModule } from './app.module';
import { PrismaService } from './prisma/prisma.service';
import { GlobalExceptionFilter } from './common/filters/global-exception.filter';
import helmet from 'helmet';
import compression from 'compression';
import rateLimit from 'express-rate-limit';
import * as express from 'express';
import { join } from 'path';

async function bootstrap() {
  // Enable rawBody for Stripe webhooks verification (available at /api/webhooks/stripe)
  const app = await NestFactory.create(AppModule, { rawBody: true });
  app.enableShutdownHooks();
  // Trust proxy (Railway/Netlify/other PaaS)
  const httpAdapter = app.getHttpAdapter();
  const instance: any = httpAdapter.getInstance?.() ?? (httpAdapter as any);
  if (instance?.set) instance.set('trust proxy', 1);
  app.use(compression());
  const limiter = rateLimit({
    windowMs: parseInt(process.env.RATE_LIMIT_WINDOW_MS || '900000', 10), // 15 minutes
    max: parseInt(process.env.RATE_LIMIT_MAX || '300', 10), // limit each IP per window
    standardHeaders: true,
    legacyHeaders: false,
  });
  app.use(limiter);

  // Enable CORS
  const allowedOrigins = process.env.FRONTEND_ORIGINS?.split(',')
    .map((s) => s.trim())
    .filter(Boolean) || [
    'http://localhost:3000',
    'http://localhost:3002',
    'http://127.0.0.1:3000',
    'http://127.0.0.1:3002',
  ];

  app.enableCors({
    origin: allowedOrigins,
    credentials: true,
    methods: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH', 'OPTIONS'],
    allowedHeaders: ['Content-Type', 'Authorization'],
  });

  // Security middleware (allow cross-origin loading of assets like images)
  app.use(
    helmet({
      crossOriginResourcePolicy: { policy: 'cross-origin' },
    }),
  );

  // Global prefix
  app.setGlobalPrefix('api');

  // Static file serving for uploaded assets
  app.use('/uploads', express.static(join(process.cwd(), 'uploads')));

  // Global validation pipe
  app.useGlobalPipes(
    new ValidationPipe({
      whitelist: true,
      transform: true,
      transformOptions: { enableImplicitConversion: true },
      forbidUnknownValues: false,
    }),
  );

  // Global exception filter for error logging
  const prisma = app.get(PrismaService);
  app.useGlobalFilters(new GlobalExceptionFilter(prisma));

  const port = process.env.PORT || 3001;
  await app.listen(port);
  console.log(`Backend server running on http://localhost:${port}`);
}
bootstrap();

import {
  Controller,
  Get,
  Post,
  Put,
  Delete,
  Body,
  Param,
  UseGuards,
  Query,
  BadRequestException,
} from '@nestjs/common';
import { AdminService } from './admin.service';
import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
import { RolesGuard } from '../auth/guards/roles.guard';
import { Roles } from '../../common/decorators/roles.decorator';
import { CurrentUser } from '../../common/decorators/current-user.decorator';
import { UpdateUserByAdminDto } from './dto/update-user.dto';
import { UserRole } from '../../common/enums';
import { ResetUserPasswordDto } from './dto/reset-user-password.dto';
import { EmailService } from '../email/email.service';

@Controller('admin')
@UseGuards(JwtAuthGuard, RolesGuard)
@Roles(UserRole.ADMIN, UserRole.SUPER_ADMIN)
export class AdminController {
  constructor(
    private readonly adminService: AdminService,
    private readonly emailService: EmailService,
  ) {}

  @Get('dashboard')
  getDashboardStats() {
    return this.adminService.getDashboardStats();
  }

  @Get('users')
  getAllUsers(
    @Query('search') search?: string,
    @Query('role') role?: UserRole,
    @Query('subscriptionPlan') subscriptionPlan?: string,
    @Query('page') page?: string,
    @Query('limit') limit?: string,
  ) {
    return this.adminService.getAllUsers({
      search,
      role,
      subscriptionPlan,
      page: page ? parseInt(page) : undefined,
      limit: limit ? parseInt(limit) : undefined,
    });
  }

  @Get('users/:id')
  getUserDetails(@Param('id') id: string) {
    return this.adminService.getUserDetails(id);
  }

  @Put('users/:id')
  updateUser(
    @Param('id') id: string,
    @Body() updateUserDto: UpdateUserByAdminDto,
  ) {
    return this.adminService.updateUser(id, updateUserDto);
  }

  @Post('users/:id/suspend')
  suspendUser(@Param('id') id: string, @CurrentUser() admin) {
    return this.adminService.suspendUser(id, admin.id);
  }

  @Post('users/:id/activate')
  activateUser(@Param('id') id: string, @CurrentUser() admin) {
    return this.adminService.activateUser(id, admin.id);
  }

  @Delete('users/:id')
  deleteUser(@Param('id') id: string, @CurrentUser() admin) {
    return this.adminService.deleteUser(id, admin.id);
  }

  @Post('users/:id/reset-password')
  resetUserPassword(
    @Param('id') id: string,
    @Body() dto: ResetUserPasswordDto,
    @CurrentUser() admin,
  ) {
    return this.adminService.resetUserPassword(id, dto, admin.id);
  }

  @Get('settings')
  getSystemSettings() {
    return this.adminService.getSystemSettings();
  }

  @Put('settings')
  updateSystemSettings(@Body() data: any) {
    return this.adminService.updateSystemSettings(data);
  }

  // SMTP testing endpoints
  @Post('settings/test-smtp')
  testSmtp(@Body() overrides: Record<string, any> = {}) {
    return this.emailService.verifySmtp(overrides);
  }

  @Post('settings/test-email')
  async testEmail(
    @Body() body: { to?: string; overrides?: Record<string, any> },
  ) {
    const to = body?.to?.trim();
    if (!to) throw new BadRequestException('Recipient email (to) is required');
    return this.emailService.sendTestEmail(to, body?.overrides || {});
  }

  @Post('settings/test-invoice-email')
  async testInvoiceEmail(
    @Body() body: { to?: string; overrides?: Record<string, any> },
  ) {
    const to = body?.to?.trim();
    if (!to) throw new BadRequestException('Recipient email (to) is required');
    return this.emailService.sendTestInvoiceEmail(to, body?.overrides || {});
  }

  @Get('settings/email-preview')
  async previewEmail(@Query('type') type?: string) {
    const t = (type as any) || 'welcome';
    return this.emailService.previewTemplate(t);
  }

  @Get('activity-logs')
  getActivityLogs(
    @Query('userId') userId?: string,
    @Query('action') action?: string,
    @Query('startDate') startDate?: string,
    @Query('endDate') endDate?: string,
    @Query('page') page?: string,
    @Query('limit') limit?: string,
  ) {
    return this.adminService.getActivityLogs({
      userId,
      action,
      startDate: startDate ? new Date(startDate) : undefined,
      endDate: endDate ? new Date(endDate) : undefined,
      page: page ? parseInt(page) : undefined,
      limit: limit ? parseInt(limit) : undefined,
    });
  }

  @Get('invoice-statistics')
  getInvoiceStatistics() {
    return this.adminService.getInvoiceStatistics();
  }

  // System Management: Backup & Maintenance
  @Get('backup/status')
  getBackupStatus() {
    return this.adminService.getBackupStatus();
  }

  @Post('backup/run')
  runBackup(@CurrentUser() admin) {
    return this.adminService.triggerBackup(admin.id);
  }

  @Post('maintenance')
  setMaintenance(@Body() body: { enabled?: boolean | string | number }) {
    const v = (body as any)?.enabled;
    const enabled = v === true || v === 'true' || v === 1 || v === '1';
    return this.adminService.setMaintenanceMode(enabled);
  }

  @Get('error-logs')
  getErrorLogs(
    @Query('search') search?: string,
    @Query('level') level?: string,
    @Query('statusCode') statusCode?: string,
    @Query('method') method?: string,
    @Query('path') path?: string,
    @Query('startDate') startDate?: string,
    @Query('endDate') endDate?: string,
    @Query('page') page?: string,
    @Query('limit') limit?: string,
  ) {
    return this.adminService.getErrorLogs({
      search,
      level,
      statusCode: statusCode ? parseInt(statusCode) : undefined,
      method,
      path,
      startDate: startDate ? new Date(startDate) : undefined,
      endDate: endDate ? new Date(endDate) : undefined,
      page: page ? parseInt(page) : undefined,
      limit: limit ? parseInt(limit) : undefined,
    });
  }

  @Delete('error-logs/:id')
  deleteErrorLog(@Param('id') id: string) {
    return this.adminService.deleteErrorLog(id);
  }

  @Delete('error-logs')
  clearErrorLogs(@Query('olderThanDays') olderThanDays?: string) {
    return this.adminService.clearErrorLogs(
      olderThanDays ? parseInt(olderThanDays) : undefined,
    );
  }
}

import {
  Injectable,
  NotFoundException,
  ForbiddenException,
  BadRequestException,
} from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import { UpdateUserByAdminDto } from './dto/update-user.dto';
import { UserRole } from '../../common/enums';
import { ResetUserPasswordDto } from './dto/reset-user-password.dto';
import * as bcrypt from 'bcryptjs';

@Injectable()
export class AdminService {
  constructor(private prisma: PrismaService) {}

  async getDashboardStats() {
    const [
      totalUsers,
      activeUsers,
      totalInvoices,
      totalRevenue,
      totalClients,
      systemSettings,
    ] = await Promise.all([
      // Total users
      this.prisma.user.count(),

      // Active users (logged in last 30 days)
      this.prisma.user.count({
        where: {
          lastLogin: {
            gte: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
          },
        },
      }),

      // Total invoices system-wide
      this.prisma.invoice.count(),

      // Total revenue system-wide
      this.prisma.invoice.aggregate({
        where: { status: 'PAID' },
        _sum: { totalAmount: true },
      }),

      // Total clients system-wide
      this.prisma.client.count(),

      // System settings (key/value aggregate)
      this.getSystemSettings(),
    ]);

    // Subscription distribution
    const subscriptionStats = await this.prisma.user.groupBy({
      by: ['subscriptionPlan'],
      _count: { subscriptionPlan: true },
    });

    // Monthly growth
    const monthlyGrowth = await this.getMonthlyUserGrowth();

    return {
      totalUsers,
      activeUsers,
      totalInvoices,
      totalRevenue: totalRevenue._sum.totalAmount || 0,
      totalClients,
      subscriptionStats: subscriptionStats.map((stat) => ({
        plan: stat.subscriptionPlan,
        count: stat._count.subscriptionPlan,
      })),
      monthlyGrowth,
      systemSettings,
    };
  }

  async getAllUsers(query?: {
    search?: string;
    role?: UserRole;
    subscriptionPlan?: string;
    page?: number;
    limit?: number;
  }) {
    const page = query?.page || 1;
    const limit = query?.limit || 20;
    const skip = (page - 1) * limit;

    const where: any = {};

    if (query?.search) {
      where.OR = [
        { email: { contains: query.search, mode: 'insensitive' } },
        { firstName: { contains: query.search, mode: 'insensitive' } },
        { lastName: { contains: query.search, mode: 'insensitive' } },
        { companyName: { contains: query.search, mode: 'insensitive' } },
      ];
    }

    if (query?.role) {
      where.role = query.role;
    }

    if (query?.subscriptionPlan) {
      where.subscriptionPlan = query.subscriptionPlan;
    }

    const [users, total] = await Promise.all([
      this.prisma.user.findMany({
        where,
        skip,
        take: limit,
        select: {
          id: true,
          email: true,
          firstName: true,
          lastName: true,
          companyName: true,
          role: true,
          subscriptionPlan: true,
          subscriptionEnd: true,
          invoiceLimit: true,
          emailVerified: true,
          isActive: true,
          createdAt: true,
          lastLogin: true,
          _count: {
            select: {
              invoices: true,
              clients: true,
            },
          },
        },
        orderBy: { createdAt: 'desc' },
      }),
      this.prisma.user.count({ where }),
    ]);

    return {
      users,
      pagination: {
        page,
        limit,
        total,
        totalPages: Math.ceil(total / limit),
      },
    };
  }

  async getUserDetails(userId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
      include: {
        _count: {
          select: {
            invoices: true,
            clients: true,
          },
        },
        invoices: {
          take: 5,
          orderBy: { createdAt: 'desc' },
          include: { client: true },
        },
        activityLogs: {
          take: 10,
          orderBy: { createdAt: 'desc' },
        },
      },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    // Calculate user statistics
    const [totalRevenue, paidInvoices, pendingInvoices] = await Promise.all([
      this.prisma.invoice.aggregate({
        where: { userId, status: 'PAID' },
        _sum: { totalAmount: true },
      }),
      this.prisma.invoice.count({
        where: { userId, status: 'PAID' },
      }),
      this.prisma.invoice.count({
        where: { userId, status: { in: ['SENT', 'VIEWED'] } },
      }),
    ]);

    return {
      user,
      stats: {
        totalRevenue: totalRevenue._sum.totalAmount || 0,
        paidInvoices,
        pendingInvoices,
        totalInvoices: user._count.invoices,
        totalClients: user._count.clients,
      },
    };
  }

  async updateUser(userId: string, updateUserDto: UpdateUserByAdminDto) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    const updatedUser = await this.prisma.user.update({
      where: { id: userId },
      data: updateUserDto,
    });

    // Log admin action
    await this.prisma.activityLog.create({
      data: {
        userId,
        action: 'USER_UPDATED_BY_ADMIN',
        entity: 'user',
        entityId: userId,
        description: `User profile updated by admin`,
        ipAddress: '0.0.0.0', // TODO: Get from request
      },
    });

    return updatedUser;
  }

  async suspendUser(userId: string, adminId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    if (user.role === UserRole.ADMIN) {
      throw new ForbiddenException('Cannot suspend admin users');
    }

    const suspendedUser = await this.prisma.user.update({
      where: { id: userId },
      data: { isActive: false },
    });

    // Log admin action
    await this.prisma.activityLog.create({
      data: {
        userId: adminId,
        action: 'USER_SUSPENDED',
        entity: 'user',
        entityId: userId,
        description: `User suspended by admin ${adminId}`,
        ipAddress: '0.0.0.0',
      },
    });

    return suspendedUser;
  }

  async activateUser(userId: string, adminId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    const activatedUser = await this.prisma.user.update({
      where: { id: userId },
      data: { isActive: true },
    });

    // Log admin action
    await this.prisma.activityLog.create({
      data: {
        userId: adminId,
        action: 'USER_ACTIVATED',
        entity: 'user',
        entityId: userId,
        description: `User activated by admin ${adminId}`,
        ipAddress: '0.0.0.0',
      },
    });

    return activatedUser;
  }

  async deleteUser(userId: string, adminId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    if (user.role === UserRole.ADMIN) {
      throw new ForbiddenException('Cannot delete admin users');
    }

    // Delete user and all related data (cascade delete)
    await this.prisma.user.delete({
      where: { id: userId },
    });

    // Log admin action
    await this.prisma.activityLog.create({
      data: {
        userId: adminId,
        action: 'USER_DELETED',
        entity: 'user',
        entityId: userId,
        description: `User ${user.email} deleted by admin`,
        ipAddress: '0.0.0.0',
      },
    });

    return { message: 'User deleted successfully' };
  }

  async resetUserPassword(
    userId: string,
    dto: ResetUserPasswordDto,
    adminId: string,
  ) {
    const user = await this.prisma.user.findUnique({ where: { id: userId } });
    if (!user) {
      throw new NotFoundException('User not found');
    }

    const hashedPassword = await bcrypt.hash(dto.newPassword, 10);
    await this.prisma.user.update({
      where: { id: userId },
      data: { password: hashedPassword },
    });

    await this.prisma.activityLog.create({
      data: {
        userId: adminId,
        action: 'USER_PASSWORD_RESET',
        entity: 'user',
        entityId: userId,
        description: `Password reset by admin ${adminId}`,
        ipAddress: '0.0.0.0',
      },
    });

    return { message: 'Password reset successfully' };
  }

  async getSystemSettings() {
    const defaults: Record<string, any> = {
      siteName: 'Invoicy',
      companyName: 'Invoicy',
      companyEmail: 'admin@invoicy.com',
      companyPhone: '',
      companyAddress: '',
      defaultCurrency: 'USD',
      defaultTaxRate: 0,
      defaultPaymentTerms: 30,
      timezone: 'UTC',
      emailNotifications: true,
      autoBackup: true,
      maintenanceMode: false,
      // Application configuration
      allowRegistration: true,
      maxUploadMB: 10,
      // Email (SMTP) defaults - editable via Admin Settings UI
      EMAIL_HOST: 'smtp.gmail.com',
      EMAIL_PORT: 587,
      EMAIL_SECURE: false,
      EMAIL_USER: '',
      EMAIL_PASSWORD: '',
      EMAIL_FROM: 'noreply@invoicy.com',
      // Provider options
      EMAIL_PROVIDER: 'SMTP', // SMTP | SENDGRID
      SENDGRID_API_KEY: '',
      EMAIL_TRACK_OPENS: true,
      EMAIL_TRACK_CLICKS: true,
      EMAIL_CONNECTION_TIMEOUT_MS: 10000,
      EMAIL_GREETING_TIMEOUT_MS: 10000,
      EMAIL_SOCKET_TIMEOUT_MS: 20000,
      // Payments (Stripe)
      STRIPE_PUBLISHABLE_KEY: '',
      STRIPE_SECRET_KEY: '',
      STRIPE_WEBHOOK_SECRET: '',
      STRIPE_PRICE_BASIC: '',
      STRIPE_PRICE_PREMIUM: '',
      // Platform fee in basis points (1% = 100 bps). Used for Connect destination charges
      STRIPE_PLATFORM_FEE_BPS: 0,
    };

    const entries = await this.prisma.systemSettings.findMany();
    const current: Record<string, any> = { ...defaults };
    for (const row of entries) {
      current[row.key] = row.value;
    }
    // Mask sensitive values
    if (
      typeof current.EMAIL_PASSWORD === 'string' &&
      current.EMAIL_PASSWORD.length > 0
    ) {
      current.EMAIL_PASSWORD = '__SECRET__';
    }
    if (
      typeof current.SENDGRID_API_KEY === 'string' &&
      current.SENDGRID_API_KEY.length > 0
    ) {
      current.SENDGRID_API_KEY = '__SECRET__';
    }
    if (
      typeof current.STRIPE_SECRET_KEY === 'string' &&
      current.STRIPE_SECRET_KEY.length > 0
    ) {
      current.STRIPE_SECRET_KEY = '__SECRET__';
    }
    if (
      typeof current.STRIPE_WEBHOOK_SECRET === 'string' &&
      current.STRIPE_WEBHOOK_SECRET.length > 0
    ) {
      current.STRIPE_WEBHOOK_SECRET = '__SECRET__';
    }
    return current;
  }

  async updateSystemSettings(data: any) {
    // Upsert each key/value pair according to SystemSettings model
    const entries = Object.entries(data || {});
    for (const [key, valueRaw] of entries) {
      // Skip updating password when sentinel is provided
      if (key === 'EMAIL_PASSWORD' && valueRaw === '__SECRET__') {
        continue;
      }

      let value = valueRaw as any;

      switch (key) {
        case 'maxUploadMB': {
          const n = Number(value);
          if (!Number.isFinite(n) || n < 1 || n > 10240) {
            throw new BadRequestException(
              'maxUploadMB must be a number between 1 and 10240',
            );
          }
          value = Math.floor(n);
          break;
        }
        case 'EMAIL_HOST': {
          const providerInput = String(
            (data?.EMAIL_PROVIDER ?? '') || '',
          ).toUpperCase();
          const host = typeof value === 'string' ? value.trim() : '';
          if (providerInput !== 'SENDGRID') {
            if (!host) throw new BadRequestException('EMAIL_HOST is required');
          }
          value = host;
          break;
        }
        case 'EMAIL_PORT': {
          const port = Number(value);
          if (!Number.isFinite(port) || port < 1 || port > 65535) {
            throw new BadRequestException(
              'EMAIL_PORT must be a number between 1 and 65535',
            );
          }
          value = port;
          break;
        }
        case 'EMAIL_SECURE': {
          value =
            value === true || value === 'true' || value === '1' || value === 1;
          break;
        }
        case 'EMAIL_PROVIDER': {
          const provider = String(value || '').toUpperCase();
          if (!['SMTP', 'SENDGRID'].includes(provider)) {
            throw new BadRequestException(
              'EMAIL_PROVIDER must be either SMTP or SENDGRID',
            );
          }
          value = provider;
          break;
        }
        case 'SENDGRID_API_KEY': {
          // Support sentinel to keep existing
          if (value === '__SECRET__') {
            continue; // skip upsert to preserve existing secret
          }
          value = String(value || '').trim();
          break;
        }
        case 'EMAIL_TRACK_OPENS':
        case 'EMAIL_TRACK_CLICKS': {
          value =
            value === true || value === 'true' || value === '1' || value === 1;
          break;
        }
        case 'EMAIL_FROM': {
          const from = String(value || '').trim();
          const emailRe = /^[^@\s]+@[^@\s]+\.[^@\s]+$/;
          if (!emailRe.test(from)) {
            throw new BadRequestException(
              'EMAIL_FROM must be a valid email address',
            );
          }
          value = from;
          break;
        }
        case 'EMAIL_CONNECTION_TIMEOUT_MS':
        case 'EMAIL_GREETING_TIMEOUT_MS':
        case 'EMAIL_SOCKET_TIMEOUT_MS': {
          const ms = Number(value);
          if (!Number.isFinite(ms) || ms < 0 || ms > 600000) {
            throw new BadRequestException(
              `${key} must be a number between 0 and 600000 milliseconds`,
            );
          }
          value = ms;
          break;
        }
        // Stripe settings
        case 'STRIPE_PUBLISHABLE_KEY': {
          value = String(value || '').trim();
          break;
        }
        case 'STRIPE_SECRET_KEY':
        case 'STRIPE_WEBHOOK_SECRET': {
          if (value === '__SECRET__') {
            continue; // keep existing
          }
          value = String(value || '').trim();
          break;
        }
        case 'STRIPE_PRICE_BASIC':
        case 'STRIPE_PRICE_PREMIUM': {
          value = String(value || '').trim();
          break;
        }
        case 'STRIPE_PLATFORM_FEE_BPS': {
          const bps = Number(value);
          if (!Number.isFinite(bps) || bps < 0 || bps > 10000) {
            throw new BadRequestException(
              'STRIPE_PLATFORM_FEE_BPS must be between 0 and 10000 (100% = 10000)',
            );
          }
          value = Math.floor(bps);
          break;
        }
        default: {
          // Coerce common boolean flags when sent as strings
          if (
            [
              'emailNotifications',
              'autoBackup',
              'maintenanceMode',
              'allowRegistration',
            ].includes(key)
          ) {
            value =
              value === true ||
              value === 'true' ||
              value === '1' ||
              value === 1;
          }
          break;
        }
      }

      await this.prisma.systemSettings.upsert({
        where: { key },
        create: { key, value: value },
        update: { value: value },
      });
    }
    // Return merged settings (with masking applied)
    return this.getSystemSettings();
  }

  async getActivityLogs(query?: {
    userId?: string;
    action?: string;
    startDate?: Date;
    endDate?: Date;
    page?: number;
    limit?: number;
  }) {
    const page = query?.page || 1;
    const limit = query?.limit || 50;
    const skip = (page - 1) * limit;

    const where: any = {};

    if (query?.userId) {
      where.userId = query.userId;
    }

    if (query?.action) {
      where.action = query.action;
    }

    if (query?.startDate && query?.endDate) {
      where.createdAt = {
        gte: query.startDate,
        lte: query.endDate,
      };
    }

    const [logs, total] = await Promise.all([
      this.prisma.activityLog.findMany({
        where,
        skip,
        take: limit,
        include: {
          user: {
            select: {
              email: true,
              firstName: true,
              lastName: true,
            },
          },
        },
        orderBy: { createdAt: 'desc' },
      }),
      this.prisma.activityLog.count({ where }),
    ]);

    return {
      logs,
      pagination: {
        page,
        limit,
        total,
        totalPages: Math.ceil(total / limit),
      },
    };
  }

  async getErrorLogs(query?: {
    search?: string;
    level?: string;
    statusCode?: number;
    method?: string;
    path?: string;
    startDate?: Date;
    endDate?: Date;
    page?: number;
    limit?: number;
  }) {
    const page = query?.page || 1;
    const limit = query?.limit || 50;
    const skip = (page - 1) * limit;

    const where: any = {};

    if (query?.level) where.level = query.level;
    if (query?.statusCode) where.statusCode = query.statusCode;
    if (query?.method) where.method = query.method;
    if (query?.path) where.path = { contains: query.path, mode: 'insensitive' };
    if (query?.search) {
      where.OR = [
        { message: { contains: query.search, mode: 'insensitive' } },
        { path: { contains: query.search, mode: 'insensitive' } },
      ];
    }
    if (query?.startDate && query?.endDate) {
      where.createdAt = { gte: query.startDate, lte: query.endDate };
    }

    const prismaAny = this.prisma as any;
    const [logs, total] = await Promise.all([
      prismaAny.errorLog.findMany({
        where,
        skip,
        take: limit,
        orderBy: { createdAt: 'desc' },
      }),
      prismaAny.errorLog.count({ where }),
    ]);

    return {
      logs,
      pagination: {
        page,
        limit,
        total,
        totalPages: Math.ceil(total / limit),
      },
    };
  }

  async deleteErrorLog(id: string) {
    const prismaAny = this.prisma as any;
    try {
      await prismaAny.errorLog.delete({ where: { id } });
      return { message: 'Error log deleted' };
    } catch (e) {
      throw new NotFoundException('Error log not found');
    }
  }

  async clearErrorLogs(olderThanDays?: number) {
    const prismaAny = this.prisma as any;
    const where: any = {};
    if (olderThanDays && olderThanDays > 0) {
      const cutoff = new Date();
      cutoff.setDate(cutoff.getDate() - olderThanDays);
      where.createdAt = { lt: cutoff };
    }
    const res = await prismaAny.errorLog.deleteMany({ where });
    return { message: 'Error logs cleared', count: res.count };
  }

  async getInvoiceStatistics() {
    const [
      totalInvoices,
      paidInvoices,
      pendingInvoices,
      overdueInvoices,
      totalRevenue,
      monthlyRevenue,
    ] = await Promise.all([
      this.prisma.invoice.count(),
      this.prisma.invoice.count({ where: { status: 'PAID' } }),
      this.prisma.invoice.count({
        where: { status: { in: ['SENT', 'VIEWED'] } },
      }),
      this.prisma.invoice.count({
        where: {
          status: { in: ['SENT', 'VIEWED'] },
          dueDate: { lt: new Date() },
        },
      }),
      this.prisma.invoice.aggregate({
        where: { status: 'PAID' },
        _sum: { totalAmount: true },
      }),
      this.getMonthlyRevenue(),
    ]);

    return {
      totalInvoices,
      paidInvoices,
      pendingInvoices,
      overdueInvoices,
      totalRevenue: totalRevenue._sum.totalAmount || 0,
      monthlyRevenue,
    };
  }

  // Helper methods
  private async getMonthlyUserGrowth() {
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const users = await this.prisma.user.findMany({
      where: {
        createdAt: { gte: sixMonthsAgo },
      },
      select: {
        createdAt: true,
      },
    });

    const grouped = {};
    users.forEach((user) => {
      const date = new Date(user.createdAt);
      const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      grouped[key] = (grouped[key] || 0) + 1;
    });

    return Object.entries(grouped).map(([month, count]) => ({
      month,
      count,
    }));
  }

  private async getMonthlyRevenue() {
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const invoices = await this.prisma.invoice.findMany({
      where: {
        status: 'PAID',
        paidAt: { gte: sixMonthsAgo },
      },
      select: {
        paidAt: true,
        totalAmount: true,
      },
    });

    const grouped = {};
    invoices.forEach((invoice) => {
      const date = new Date(invoice.paidAt!);
      const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      if (!grouped[key]) {
        grouped[key] = { month: key, amount: 0 };
      }
      grouped[key].amount += invoice.totalAmount;
    });

    return Object.values(grouped);
  }

  // System management: backup + maintenance
  async triggerBackup(adminId: string) {
    const now = new Date();
    const backupId = `bkp_${now.getTime().toString(36)}_${Math.random().toString(36).slice(2, 8)}`;
    const timestamp = now.toISOString();
    const safeTs = timestamp.replace(/[:.]/g, '-');
    const file = `backup_${safeTs}.json`;

    // Persist lightweight metadata in SystemSettings
    await this.prisma.$transaction([
      this.prisma.systemSettings.upsert({
        where: { key: 'LAST_BACKUP_AT' },
        create: { key: 'LAST_BACKUP_AT', value: timestamp as any },
        update: { value: timestamp as any },
      }),
      this.prisma.systemSettings.upsert({
        where: { key: 'LAST_BACKUP_ID' },
        create: { key: 'LAST_BACKUP_ID', value: backupId as any },
        update: { value: backupId as any },
      }),
      this.prisma.systemSettings.upsert({
        where: { key: 'LAST_BACKUP_FILE' },
        create: { key: 'LAST_BACKUP_FILE', value: file as any },
        update: { value: file as any },
      }),
    ]);

    // Log admin action
    await this.prisma.activityLog.create({
      data: {
        userId: adminId,
        action: 'SYSTEM_BACKUP_TRIGGERED',
        entity: 'system',
        entityId: backupId,
        description: `System backup triggered by admin ${adminId}`,
      },
    });

    return { success: true, backupId, file, createdAt: timestamp };
  }

  async getBackupStatus() {
    const rows = await this.prisma.systemSettings.findMany({
      where: {
        key: {
          in: [
            'LAST_BACKUP_AT',
            'LAST_BACKUP_ID',
            'LAST_BACKUP_FILE',
            'maintenanceMode',
          ],
        },
      },
    });
    const map = new Map(rows.map((r) => [r.key, r.value])) as Map<string, any>;
    return {
      lastBackupAt: map.get('LAST_BACKUP_AT') || null,
      lastBackupId: map.get('LAST_BACKUP_ID') || null,
      lastBackupFile: map.get('LAST_BACKUP_FILE') || null,
      maintenanceMode: !!map.get('maintenanceMode'),
    };
  }

  async setMaintenanceMode(enabled: boolean) {
    await this.prisma.systemSettings.upsert({
      where: { key: 'maintenanceMode' },
      create: { key: 'maintenanceMode', value: enabled as any },
      update: { value: enabled as any },
    });
    return { maintenanceMode: enabled };
  }
}

import { Controller, Get, UseGuards, Query } from '@nestjs/common';
import { AnalyticsService } from './analytics.service';
import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
import { CurrentUser } from '../../common/decorators/current-user.decorator';

@Controller('analytics')
@UseGuards(JwtAuthGuard)
export class AnalyticsController {
  constructor(private readonly analyticsService: AnalyticsService) {}

  @Get('dashboard')
  getDashboardStats(@CurrentUser() user) {
    return this.analyticsService.getDashboardStats(user.id);
  }

  @Get('revenue')
  getRevenueAnalytics(
    @CurrentUser() user,
    @Query('period') period?: 'week' | 'month' | 'year',
  ) {
    return this.analyticsService.getRevenueAnalytics(
      user.id,
      period || 'month',
    );
  }

  @Get('invoices')
  getInvoiceAnalytics(@CurrentUser() user) {
    return this.analyticsService.getInvoiceAnalytics(user.id);
  }

  @Get('clients')
  getClientAnalytics(@CurrentUser() user) {
    return this.analyticsService.getClientAnalytics(user.id);
  }

  @Get('payments')
  getPaymentAnalytics(@CurrentUser() user) {
    return this.analyticsService.getPaymentAnalytics(user.id);
  }
}

import { Injectable } from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import { InvoiceStatus, PaymentStatus } from '../../common/enums';

@Injectable()
export class AnalyticsService {
  constructor(private prisma: PrismaService) {}

  async getDashboardStats(userId: string) {
    const currentDate = new Date();
    const currentMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      1,
    );
    const lastMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() - 1,
      1,
    );
    const currentYear = new Date(currentDate.getFullYear(), 0, 1);

    const [
      totalInvoices,
      totalClients,
      totalRevenue,
      pendingAmount,
      monthlyRevenue,
      overdueInvoices,
      overdueAmountAgg,
      recentInvoices,
      topClients,
    ] = await Promise.all([
      // Total invoices count
      this.prisma.invoice.count({ where: { userId } }),

      // Total clients count
      this.prisma.client.count({ where: { userId, isActive: true } }),

      // Total revenue (paid invoices)
      this.prisma.invoice.aggregate({
        where: { userId, status: InvoiceStatus.PAID },
        _sum: { totalAmount: true },
      }),

      // Pending amount (includes SENT, VIEWED, and OVERDUE)
      this.prisma.invoice.aggregate({
        where: {
          userId,
          status: {
            in: [
              InvoiceStatus.SENT,
              InvoiceStatus.VIEWED,
              InvoiceStatus.OVERDUE,
            ],
          },
        },
        _sum: { totalAmount: true },
      }),

      // Monthly revenue
      this.prisma.invoice.aggregate({
        where: {
          userId,
          status: InvoiceStatus.PAID,
          paidAt: { gte: currentMonth },
        },
        _sum: { totalAmount: true },
      }),

      // Overdue invoices count (explicit OVERDUE status)
      this.prisma.invoice.count({
        where: {
          userId,
          status: InvoiceStatus.OVERDUE,
        },
      }),

      // Overdue amount (sum of OVERDUE invoices)
      this.prisma.invoice.aggregate({
        where: {
          userId,
          status: InvoiceStatus.OVERDUE,
        },
        _sum: { totalAmount: true },
      }),

      // Recent invoices
      this.prisma.invoice.findMany({
        where: { userId },
        take: 5,
        orderBy: { createdAt: 'desc' },
        include: { client: true },
      }),

      // Top clients by revenue
      this.prisma.client.findMany({
        where: { userId },
        take: 5,
        include: {
          invoices: {
            where: { status: InvoiceStatus.PAID },
            select: { totalAmount: true },
          },
        },
      }),
    ]);

    // Calculate top clients with total revenue
    const topClientsWithRevenue = topClients
      .map((client) => ({
        id: client.id,
        name: client.name,
        email: client.email,
        totalRevenue: client.invoices.reduce(
          (sum, inv) => sum + inv.totalAmount,
          0,
        ),
        invoiceCount: client.invoices.length,
      }))
      .sort((a, b) => b.totalRevenue - a.totalRevenue)
      .slice(0, 5);

    return {
      totalInvoices,
      totalClients,
      totalRevenue: totalRevenue._sum.totalAmount || 0,
      pendingAmount: pendingAmount._sum.totalAmount || 0,
      monthlyRevenue: monthlyRevenue._sum.totalAmount || 0,
      overdueInvoices,
      overdueAmount: overdueAmountAgg._sum.totalAmount || 0,
      recentInvoices,
      topClients: topClientsWithRevenue,
    };
  }

  async getRevenueAnalytics(
    userId: string,
    period: 'week' | 'month' | 'year' = 'month',
  ) {
    const currentDate = new Date();
    let startDate: Date;
    let groupBy: string;

    switch (period) {
      case 'week':
        startDate = new Date(currentDate.getTime() - 7 * 24 * 60 * 60 * 1000);
        groupBy = 'day';
        break;
      case 'month':
        startDate = new Date(
          currentDate.getFullYear(),
          currentDate.getMonth(),
          1,
        );
        groupBy = 'day';
        break;
      case 'year':
        startDate = new Date(currentDate.getFullYear(), 0, 1);
        groupBy = 'month';
        break;
    }

    const invoices = await this.prisma.invoice.findMany({
      where: {
        userId,
        createdAt: { gte: startDate },
      },
      select: {
        createdAt: true,
        totalAmount: true,
        status: true,
        paidAt: true,
      },
      orderBy: { createdAt: 'asc' },
    });

    // Group data by period
    const revenueData = this.groupRevenueByPeriod(invoices, groupBy);

    // Calculate growth rate
    const currentPeriodRevenue = invoices
      .filter((inv) => inv.status === InvoiceStatus.PAID)
      .reduce((sum, inv) => sum + inv.totalAmount, 0);

    const previousPeriodInvoices = await this.prisma.invoice.findMany({
      where: {
        userId,
        status: InvoiceStatus.PAID,
        paidAt: {
          gte: new Date(
            startDate.getTime() - (currentDate.getTime() - startDate.getTime()),
          ),
          lt: startDate,
        },
      },
      select: { totalAmount: true },
    });

    const previousPeriodRevenue = previousPeriodInvoices.reduce(
      (sum, inv) => sum + inv.totalAmount,
      0,
    );

    const growthRate =
      previousPeriodRevenue > 0
        ? ((currentPeriodRevenue - previousPeriodRevenue) /
            previousPeriodRevenue) *
          100
        : 0;

    return {
      period,
      revenueData,
      totalRevenue: currentPeriodRevenue,
      growthRate: growthRate.toFixed(2),
      invoiceCount: invoices.length,
      paidInvoices: invoices.filter((inv) => inv.status === InvoiceStatus.PAID)
        .length,
    };
  }

  async getInvoiceAnalytics(userId: string) {
    const [statusDistribution, monthlyInvoices, averagePaymentTime] =
      await Promise.all([
        // Invoice status distribution
        this.prisma.invoice.groupBy({
          by: ['status'],
          where: { userId },
          _count: { status: true },
        }),

        // Monthly invoice creation trend
        this.getMonthlyInvoiceTrend(userId),

        // Average payment time
        this.getAveragePaymentTime(userId),
      ]);

    return {
      statusDistribution: statusDistribution.map((item) => ({
        status: item.status,
        count: item._count.status,
      })),
      monthlyInvoices,
      averagePaymentTime,
    };
  }

  async getClientAnalytics(userId: string) {
    const [
      clientTypeDistribution,
      clientGrowth,
      mostActiveClients,
      clientRetention,
    ] = await Promise.all([
      // Client type distribution
      this.prisma.client.groupBy({
        by: ['clientType'],
        where: { userId },
        _count: { clientType: true },
      }),

      // Monthly client growth
      this.getMonthlyClientGrowth(userId),

      // Most active clients
      this.prisma.client.findMany({
        where: { userId },
        include: {
          _count: { select: { invoices: true } },
          invoices: {
            where: { status: InvoiceStatus.PAID },
            select: { totalAmount: true },
          },
        },
        take: 10,
      }),

      // Client retention rate
      this.calculateClientRetention(userId),
    ]);

    const sortedActiveClients = mostActiveClients
      .map((client) => ({
        id: client.id,
        name: client.name,
        companyName: client.companyName,
        invoiceCount: client._count.invoices,
        totalRevenue: client.invoices.reduce(
          (sum, inv) => sum + inv.totalAmount,
          0,
        ),
      }))
      .sort((a, b) => b.invoiceCount - a.invoiceCount);

    return {
      clientTypeDistribution: clientTypeDistribution.map((item) => ({
        type: item.clientType,
        count: item._count.clientType,
      })),
      clientGrowth,
      mostActiveClients: sortedActiveClients,
      clientRetention,
    };
  }

  async getPaymentAnalytics(userId: string) {
    const [
      paymentMethodDistribution,
      monthlyPayments,
      paymentStatusDistribution,
      averagePaymentAmount,
    ] = await Promise.all([
      // Payment method distribution
      this.prisma.payment.groupBy({
        by: ['paymentMethod'],
        where: {
          invoice: { userId },
          status: PaymentStatus.COMPLETED,
        },
        _count: { paymentMethod: true },
        _sum: { amount: true },
      }),

      // Monthly payment trend
      this.getMonthlyPaymentTrend(userId),

      // Payment status distribution
      this.prisma.payment.groupBy({
        by: ['status'],
        where: { invoice: { userId } },
        _count: { status: true },
      }),

      // Average payment amount
      this.prisma.payment.aggregate({
        where: {
          invoice: { userId },
          status: PaymentStatus.COMPLETED,
        },
        _avg: { amount: true },
      }),
    ]);

    return {
      paymentMethodDistribution: paymentMethodDistribution.map((item) => ({
        method: item.paymentMethod,
        count: item._count.paymentMethod,
        totalAmount: item._sum.amount || 0,
      })),
      monthlyPayments,
      paymentStatusDistribution: paymentStatusDistribution.map((item) => ({
        status: item.status,
        count: item._count.status,
      })),
      averagePaymentAmount: averagePaymentAmount._avg.amount || 0,
    };
  }

  // Helper methods
  private groupRevenueByPeriod(invoices: any[], groupBy: string) {
    const grouped = {};

    invoices.forEach((invoice) => {
      let key: string;
      const date = new Date(invoice.createdAt);

      if (groupBy === 'day') {
        key = date.toISOString().split('T')[0];
      } else {
        key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      }

      if (!grouped[key]) {
        grouped[key] = {
          date: key,
          total: 0,
          paid: 0,
          pending: 0,
          count: 0,
        };
      }

      grouped[key].total += invoice.totalAmount;
      grouped[key].count += 1;

      if (invoice.status === InvoiceStatus.PAID) {
        grouped[key].paid += invoice.totalAmount;
      } else if (
        [
          InvoiceStatus.SENT,
          InvoiceStatus.VIEWED,
          InvoiceStatus.OVERDUE,
        ].includes(invoice.status)
      ) {
        grouped[key].pending += invoice.totalAmount;
      }
    });

    return Object.values(grouped);
  }

  private async getMonthlyInvoiceTrend(userId: string) {
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const invoices = await this.prisma.invoice.findMany({
      where: {
        userId,
        createdAt: { gte: sixMonthsAgo },
      },
      select: {
        createdAt: true,
      },
    });

    const grouped = {};
    invoices.forEach((invoice) => {
      const date = new Date(invoice.createdAt);
      const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      grouped[key] = (grouped[key] || 0) + 1;
    });

    return Object.entries(grouped).map(([month, count]) => ({
      month,
      count,
    }));
  }

  private async getAveragePaymentTime(userId: string) {
    const paidInvoices = await this.prisma.invoice.findMany({
      where: {
        userId,
        status: InvoiceStatus.PAID,
        paidAt: { not: null },
      },
      select: {
        invoiceDate: true,
        paidAt: true,
      },
    });

    if (paidInvoices.length === 0) return 0;

    const totalDays = paidInvoices.reduce((sum, invoice) => {
      const days = Math.floor(
        (invoice.paidAt!.getTime() - invoice.invoiceDate.getTime()) /
          (1000 * 60 * 60 * 24),
      );
      return sum + days;
    }, 0);

    return Math.round(totalDays / paidInvoices.length);
  }

  private async getMonthlyClientGrowth(userId: string) {
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const clients = await this.prisma.client.findMany({
      where: {
        userId,
        createdAt: { gte: sixMonthsAgo },
      },
      select: {
        createdAt: true,
      },
    });

    const grouped = {};
    clients.forEach((client) => {
      const date = new Date(client.createdAt);
      const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      grouped[key] = (grouped[key] || 0) + 1;
    });

    return Object.entries(grouped).map(([month, count]) => ({
      month,
      count,
    }));
  }

  private async calculateClientRetention(userId: string) {
    const threeMonthsAgo = new Date();
    threeMonthsAgo.setMonth(threeMonthsAgo.getMonth() - 3);

    const activeClients = await this.prisma.client.count({
      where: {
        userId,
        invoices: {
          some: {
            createdAt: { gte: threeMonthsAgo },
          },
        },
      },
    });

    const totalClients = await this.prisma.client.count({
      where: { userId },
    });

    return totalClients > 0 ? (activeClients / totalClients) * 100 : 0;
  }

  private async getMonthlyPaymentTrend(userId: string) {
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const payments = await this.prisma.payment.findMany({
      where: {
        invoice: { userId },
        status: PaymentStatus.COMPLETED,
        paymentDate: { gte: sixMonthsAgo },
      },
      select: {
        paymentDate: true,
        amount: true,
      },
    });

    const grouped = {};
    payments.forEach((payment) => {
      const date = new Date(payment.paymentDate);
      const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      if (!grouped[key]) {
        grouped[key] = { month: key, amount: 0, count: 0 };
      }
      grouped[key].amount += payment.amount;
      grouped[key].count += 1;
    });

    return Object.values(grouped);
  }
}

import {
