  // Utilities for Admin to test SMTP configuration without persisting
  private async buildTransporterWithOverrides(
    overrides?: Record<string, any>,
  ): Promise<nodemailer.Transporter> {
    const base = await this.loadEmailSettings();
    const o = overrides || {};

    const host = (o.EMAIL_HOST ?? base.host) as string;
    const port = this.toNum(o.EMAIL_PORT ?? base.port, base.port);
    const secure = this.toBool(o.EMAIL_SECURE ?? base.secure, base.secure);

    // Username: allow explicit empty string to clear auth
    const user = o.EMAIL_USER === '' ? undefined : (o.EMAIL_USER ?? base.user);
    // Password: respect sentinel to keep existing
    let pass: string | undefined;
    if (Object.prototype.hasOwnProperty.call(o, 'EMAIL_PASSWORD')) {
      pass =
        o.EMAIL_PASSWORD === '__SECRET__'
          ? base.pass
          : o.EMAIL_PASSWORD || undefined;
    } else {
      pass = base.pass;
    }

    const from = (o.EMAIL_FROM ?? base.from) as string;
    const connectionTimeout = this.toNum(
      o.EMAIL_CONNECTION_TIMEOUT_MS ?? base.connectionTimeout,
      base.connectionTimeout,
    );
    const greetingTimeout = this.toNum(
      o.EMAIL_GREETING_TIMEOUT_MS ?? base.greetingTimeout,
      base.greetingTimeout,
    );
    const socketTimeout = this.toNum(
      o.EMAIL_SOCKET_TIMEOUT_MS ?? base.socketTimeout,
      base.socketTimeout,
    );

    return nodemailer.createTransport({
      host,
      port,
      secure,
      auth: user && pass ? { user, pass } : undefined,
      connectionTimeout,
      greetingTimeout,
      socketTimeout,
      logger: base.logger,
    });
  }

  async verifySmtp(
    overrides?: Record<string, any>,
  ): Promise<{ success: boolean; error?: string }> {
    const transporter = await this.buildTransporterWithOverrides(overrides);
    try {
      await transporter.verify();
      return { success: true };
    } catch (e: any) {
      return {
        success: false,
        error: e?.message || 'SMTP verification failed',
      };
    }
  }

  async sendTestEmail(to: string, overrides?: Record<string, any>) {
    if (!to) {
      throw new Error('Recipient email (to) is required');
    }
    const base = await this.loadEmailSettings();

    const providerOverride = (overrides?.EMAIL_PROVIDER || base.provider) as
      | 'SMTP'
      | 'SENDGRID';
    const from = (overrides?.EMAIL_FROM ?? base.from) as string;

    const siteName = await this.getSiteName();
    const subject = `${siteName} Email Test`;
    const html = `
      <!DOCTYPE html>
      <html>
      <body style="font-family: Arial, sans-serif; line-height: 1.6;">
        <h2>Email Test Successful</h2>
        <p>This is a test email sent from ${siteName} using the currently configured provider (${providerOverride}).</p>
        <p>Time: ${new Date().toISOString()}</p>
      </body>
      </html>
    `;

    if (providerOverride === 'SENDGRID') {
      try {
        const sgMail = require('@sendgrid/mail');
        const apiKey = (overrides?.SENDGRID_API_KEY || base.sendgridApiKey) as
          | string
          | undefined;
        if (!apiKey) {
          throw new Error(
            'SENDGRID_API_KEY is required to send test email with SendGrid',
          );
        }
        sgMail.setApiKey(apiKey);
        const msg = {
          to,
          from: { email: from, name: `${siteName} Test` },
          subject,
          html,
          trackingSettings: {
            clickTracking: { enable: base.trackClicks },
            openTracking: { enable: base.trackOpens },
          },
        } as any;
        const [response] = await sgMail.send(msg);
        return {
          success: true,
          messageId: response?.headers?.['x-message-id'],
        };
      } catch (e: any) {
        if (e?.code === 'MODULE_NOT_FOUND') {
          throw new Error(
            'SendGrid test requested but @sendgrid/mail is not installed',
          );
        }
        throw e;
      }
    }

    // Default to SMTP test
    const transporter = await this.buildTransporterWithOverrides(overrides);
    try {
      const info = await transporter.sendMail({ from, to, subject, html });
      return { success: true, messageId: info.messageId };
    } catch (e) {
      throw e;
    }
  }
}

import { Injectable, Logger } from '@nestjs/common';
import { Cron, CronExpression } from '@nestjs/schedule';
import { PrismaService } from '../../prisma/prisma.service';
import { InvoiceStatus, HistoryAction } from '../../common/enums';
import { EmailService } from '../email/email.service';
import { NotificationsService } from '../notifications/notifications.service';

@Injectable()
export class InvoiceOverdueScheduler {
  private readonly logger = new Logger(InvoiceOverdueScheduler.name);

  constructor(
    private readonly prisma: PrismaService,
    private readonly emailService: EmailService,
    private readonly notifications: NotificationsService,
  ) {}

  // Runs hourly; adjust as needed
  @Cron(CronExpression.EVERY_HOUR)
  async markOverdueInvoices() {
    const now = new Date();

    const candidates = await this.prisma.invoice.findMany({
      where: {
        status: { in: [InvoiceStatus.SENT, InvoiceStatus.VIEWED] },
        dueDate: { lt: now },
      },
      include: { client: true, user: true },
    });

    if (candidates.length === 0) return;

    const ids = candidates.map((c) => c.id);

    await this.prisma.$transaction([
      this.prisma.invoice.updateMany({
        where: { id: { in: ids } },
        data: { status: InvoiceStatus.OVERDUE },
      }),
      this.prisma.invoiceHistory.createMany({
        data: ids.map((id) => ({
          invoiceId: id,
          action: HistoryAction.STATUS_CHANGED,
          description: 'Status changed to OVERDUE (auto)',
          performedBy: null,
        })),
      }),
    ]);

    this.logger.log(`Marked ${ids.length} invoice(s) as OVERDUE`);

    // Send overdue reminders (one-time per transition) gated by user preferences
    for (const invoice of candidates) {
      const shouldSend = !!(
        invoice.user?.emailNotificationsEnabled &&
        invoice.user?.emailNotifyInvoiceOverdue
      );
      const daysOverdue = Math.max(
        0,
        Math.floor(
          (now.getTime() - invoice.dueDate.getTime()) / (1000 * 60 * 60 * 24),
        ),
      );

      let description: string;
      if (shouldSend) {
        try {
          await this.emailService.sendPaymentReminderEmail(
            invoice as any,
            daysOverdue,
          );
          description = `Overdue reminder email sent to ${invoice.client.email}`;
        } catch (err) {
          this.logger.error(
            `Failed to send overdue reminder for invoice ${invoice.id}:`,
            err,
          );
          description = 'Overdue reminder email sending failed';
        }
      } else {
        description = 'Overdue reminder email skipped by user preferences';
      }

      // Record reminder history regardless of send/skip outcome
      try {
        await this.prisma.invoiceHistory.create({
          data: {
            invoiceId: invoice.id,
            action: HistoryAction.REMINDER_SENT,
            description,
            performedBy: null,
          },
        });
      } catch (e) {
        // Best-effort logging; do not throw
        this.logger.error(
          `Failed to create reminder history for invoice ${invoice.id}:`,
          e,
        );
      }

      // Emit notification event per invoice processed
      try {
        this.notifications.emit(invoice.userId, 'invoice.overdue', {
          id: invoice.id,
          daysOverdue,
          reminder: description,
        });
      } catch {}
    }
  }
}

import {
  Controller,
  Get,
  Post,
  Body,
  Patch,
  Param,
  Delete,
  UseGuards,
  Query,
  Res,
  HttpStatus,
} from '@nestjs/common';
import type { Response } from 'express';
import { InvoicesService } from './invoices.service';
import { CreateInvoiceDto } from './dto/create-invoice.dto';
import { UpdateInvoiceDto } from './dto/update-invoice.dto';
import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
import { EmailVerifiedGuard } from '../auth/guards/email-verified.guard';
import { CurrentUser } from '../../common/decorators/current-user.decorator';
import { InvoiceStatus } from '../../common/enums';

@Controller('invoices')
@UseGuards(JwtAuthGuard, EmailVerifiedGuard)
export class InvoicesController {
  constructor(private readonly invoicesService: InvoicesService) {}

  @Post()
  create(@CurrentUser() user, @Body() createInvoiceDto: CreateInvoiceDto) {
    return this.invoicesService.create(user.id, createInvoiceDto);
  }

  @Get('statistics')
  getStatistics(@CurrentUser() user) {
    return this.invoicesService.getStatistics(user.id);
  }

  @Get()
  findAll(
    @CurrentUser() user,
    @Query('status') status?: InvoiceStatus,
    @Query('clientId') clientId?: string,
    @Query('search') search?: string,
    @Query('startDate') startDate?: string,
    @Query('endDate') endDate?: string,
    @Query('sortBy') sortBy?: string,
    @Query('sortDir') sortDir?: 'asc' | 'desc',
    @Query('page') page?: string,
    @Query('limit') limit?: string,
  ) {
    const query = {
      status,
      clientId,
      search,
      startDate: startDate ? new Date(startDate) : undefined,
      endDate: endDate ? new Date(endDate) : undefined,
      sortBy,
      sortDir,
      page: page ? parseInt(page, 10) : undefined,
      limit: limit ? parseInt(limit, 10) : undefined,
    };
    return this.invoicesService.findAll(user.id, query);
  }

  @Get(':id')
  findOne(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.findOne(id, user.id);
  }

  @Patch(':id')
  update(
    @Param('id') id: string,
    @CurrentUser() user,
    @Body() updateInvoiceDto: UpdateInvoiceDto,
  ) {
    return this.invoicesService.update(id, user.id, updateInvoiceDto);
  }

  @Delete(':id')
  remove(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.remove(id, user.id);
  }

  @Patch(':id/status')
  updateStatus(
    @Param('id') id: string,
    @CurrentUser() user,
    @Body('status') status: InvoiceStatus,
  ) {
    return this.invoicesService.updateStatus(id, user.id, status);
  }

  @Post(':id/send')
  sendInvoice(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.sendInvoice(id, user.id);
  }

  @Post('bulk/send')
  sendInvoicesBulk(@Body('ids') ids: string[], @CurrentUser() user) {
    return this.invoicesService.sendInvoicesBulk(ids, user.id);
  }

  @Post('bulk/status')
  updateStatusBulk(
    @Body('ids') ids: string[],
    @Body('status') status: InvoiceStatus,
    @CurrentUser() user,
  ) {
    return this.invoicesService.updateStatusBulk(ids, user.id, status);
  }

  @Post('bulk/mark-paid')
  markPaidBulk(@Body('ids') ids: string[], @CurrentUser() user) {
    return this.invoicesService.markPaidBulk(ids, user.id);
  }

  @Post('bulk/delete')
  deleteBulk(@Body('ids') ids: string[], @CurrentUser() user) {
    return this.invoicesService.removeBulk(ids, user.id);
  }

  @Get(':id/download')
  async downloadPdf(
    @Param('id') id: string,
    @CurrentUser() user,
    @Res() res: Response,
    @Query('template') template?: string,
    @Query('color') color?: string,
    @Query('accentColor') accentColor?: string,
    @Query('headerBorderColor') headerBorderColor?: string,
    @Query('tableHeaderColor') tableHeaderColor?: string,
    @Query('font') font?: string,
    @Query('layout') layout?: string,
    @Query('footer') footer?: string,
    @Query('pageSize') pageSize?: string,
    @Query('margins') margins?: string,
    @Query('watermarkText') watermarkText?: string,
    @Query('showSignature') showSignature?: string,
    @Query('customFields') customFields?: string,
    @Query('logoSize') logoSize?: string,
    @Query('headerStyle') headerStyle?: string,
  ) {
    try {
      // Debug log for tracing issues in production

      console.log('GET /invoices/:id/download', {
        id,
        userId: user?.id,
        template,
        color,
        accentColor,
        headerBorderColor,
        tableHeaderColor,
        font,
        layout,
        footer,
        pageSize,
        margins,
        watermarkText,
        showSignature,
        logoSize,
        headerStyle,
      });

      const options = {
        template,
        colorScheme: color, // legacy
        accentColor: accentColor || color,
        headerBorderColor,
        tableHeaderColor,
        font,
        layout,
        footer,
        pageSize,
        margins,
        watermarkText,
        showSignature:
          typeof showSignature === 'string'
            ? ['true', '1', 'yes', 'on'].includes(showSignature.toLowerCase())
            : undefined,
        customFields: (() => {
          try {
            return customFields ? JSON.parse(customFields) : undefined;
          } catch {
            return undefined;
          }
        })(),
        logoSize,
        headerStyle,
      } as any;
      const pdfBuffer = await this.invoicesService.downloadPdf(
        id,
        user.id,
        options,
      );

      console.log('PDF generated, bytes:', pdfBuffer?.length ?? 0);

      res.set({
        'Content-Type': 'application/pdf',
        'Content-Disposition': `attachment; filename="invoice-${id}.pdf"`,
      });
      res.status(HttpStatus.OK);
      res.end(pdfBuffer);
    } catch (err: any) {
      console.error(
        'Failed to generate/download invoice PDF:',
        err?.message || err,
      );
      try {
        return res.status(HttpStatus.INTERNAL_SERVER_ERROR).json({
          message: 'Failed to generate invoice PDF',
          error: err?.message || 'Unknown error',
        });
      } catch {
        // If response has already started, just end it
        try {
          res.end();
        } catch {}
      }
    }
  }

  @Post(':id/duplicate')
  duplicateInvoice(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.duplicateInvoice(id, user.id);
  }

  @Post(':id/cancel')
  cancelInvoice(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.cancelInvoice(id, user.id);
  }

  // Sharing endpoints
  @Patch(':id/share')
  updateShare(
    @Param('id') id: string,
    @CurrentUser() user,
    @Body('enable') enable?: boolean,
  ) {
    return this.invoicesService.updateShare(id, user.id, { enable });
  }

  @Post(':id/share/regenerate')
  regenerateShare(@Param('id') id: string, @CurrentUser() user) {
    return this.invoicesService.updateShare(id, user.id, { regenerate: true });
  }
}

import { Test, TestingModule } from '@nestjs/testing';
import { NotFoundException } from '@nestjs/common';
import { InvoicesService } from './invoices.service';
import { PrismaService } from '../../prisma/prisma.service';
import { PdfService } from './pdf.service';
import { EmailService } from '../email/email.service';
import { NotificationsService } from '../notifications/notifications.service';
import { InvoiceStatus } from '@prisma/client';

// Simple helpers
const makeInvoice = (overrides: Partial<any> = {}) => ({
  id: 'inv_1',
  userId: 'user_1',
  client: { email: 'client@example.com' },
  items: [],
  user: {
    emailNotificationsEnabled: true,
    emailNotifyNewInvoice: true,
  },
  ...overrides,
});

describe('InvoicesService.sendInvoice', () => {
  let service: InvoicesService;
  let prisma: jest.Mocked<any>;
  let pdf: jest.Mocked<PdfService>;
  let email: jest.Mocked<EmailService>;

  beforeEach(async () => {
    prisma = {
      invoice: {
        findFirst: jest.fn(),
        update: jest.fn(),
      },
      invoiceHistory: {
        create: jest.fn(),
      },
    } as any;

    pdf = {
      generateInvoicePdf: jest.fn(),
    } as any;

    email = {
      sendInvoiceEmail: jest.fn(),
    } as any;

    const moduleRef: TestingModule = await Test.createTestingModule({
      providers: [
        InvoicesService,
        { provide: PrismaService, useValue: prisma },
        { provide: PdfService, useValue: pdf },
        { provide: EmailService, useValue: email },
        { provide: NotificationsService, useValue: { emit: jest.fn() } },
      ],
    }).compile();

    service = moduleRef.get<InvoicesService>(InvoicesService);
  });

  afterEach(() => {
    jest.resetAllMocks();
  });

  it('sends email when preferences enabled', async () => {
    const invoice = makeInvoice();
    prisma.invoice.findFirst.mockResolvedValue(invoice);
    pdf.generateInvoicePdf.mockResolvedValue(Buffer.from('PDF'));
    email.sendInvoiceEmail.mockResolvedValue(undefined);
    prisma.invoice.update.mockResolvedValue({});
    prisma.invoiceHistory.create.mockResolvedValue({});

    const res = await service.sendInvoice('inv_1', 'user_1');

    expect(res).toEqual({ message: 'Invoice sent successfully' });
    expect(prisma.invoice.findFirst).toHaveBeenCalledWith({
      where: { id: 'inv_1', userId: 'user_1' },
      include: { client: true, items: true, user: true },
    });
    expect(pdf.generateInvoicePdf).toHaveBeenCalled();
    expect(email.sendInvoiceEmail).toHaveBeenCalledTimes(1);
    expect(prisma.invoice.update).toHaveBeenCalledWith({
      where: { id: 'inv_1' },
      data: { status: InvoiceStatus.SENT, sentAt: expect.any(Date) },
    });
    expect(prisma.invoiceHistory.create).toHaveBeenCalledWith({
      data: expect.objectContaining({
        invoiceId: 'inv_1',
        action: expect.any(String),
        description: expect.stringContaining('Invoice sent to'),
        performedBy: 'user_1',
      }),
    });
  });

  it('skips email when preferences disabled', async () => {
    const invoice = makeInvoice({
      user: { emailNotificationsEnabled: false, emailNotifyNewInvoice: true },
    });
    prisma.invoice.findFirst.mockResolvedValue(invoice);
    prisma.invoice.update.mockResolvedValue({});
    prisma.invoiceHistory.create.mockResolvedValue({});

    const res = await service.sendInvoice('inv_1', 'user_1');

    expect(res).toEqual({ message: 'Invoice sent successfully' });
    expect(pdf.generateInvoicePdf).not.toHaveBeenCalled();
    expect(email.sendInvoiceEmail).not.toHaveBeenCalled();
    expect(prisma.invoice.update).toHaveBeenCalledWith({
      where: { id: 'inv_1' },
      data: { status: InvoiceStatus.SENT, sentAt: expect.any(Date) },
    });
    expect(prisma.invoiceHistory.create).toHaveBeenCalledWith({
      data: expect.objectContaining({
        description: expect.stringContaining('skipped by user preferences'),
      }),
    });
  });

  it('skips email when emailNotifyNewInvoice disabled', async () => {
    const invoice = makeInvoice({
      user: { emailNotificationsEnabled: true, emailNotifyNewInvoice: false },
    });
    prisma.invoice.findFirst.mockResolvedValue(invoice);
    prisma.invoice.update.mockResolvedValue({});
    prisma.invoiceHistory.create.mockResolvedValue({});

    const res = await service.sendInvoice('inv_1', 'user_1');

    expect(res).toEqual({ message: 'Invoice sent successfully' });
    expect(pdf.generateInvoicePdf).not.toHaveBeenCalled();
    expect(email.sendInvoiceEmail).not.toHaveBeenCalled();
    expect(prisma.invoice.update).toHaveBeenCalledWith({
      where: { id: 'inv_1' },
      data: { status: InvoiceStatus.SENT, sentAt: expect.any(Date) },
    });
    expect(prisma.invoiceHistory.create).toHaveBeenCalledWith({
      data: expect.objectContaining({
        description: expect.stringContaining('skipped by user preferences'),
      }),
    });
  });

  it('skips email when both preferences disabled', async () => {
    const invoice = makeInvoice({
      user: { emailNotificationsEnabled: false, emailNotifyNewInvoice: false },
    });
    prisma.invoice.findFirst.mockResolvedValue(invoice);
    prisma.invoice.update.mockResolvedValue({});
    prisma.invoiceHistory.create.mockResolvedValue({});

    const res = await service.sendInvoice('inv_1', 'user_1');

    expect(res).toEqual({ message: 'Invoice sent successfully' });
    expect(pdf.generateInvoicePdf).not.toHaveBeenCalled();
    expect(email.sendInvoiceEmail).not.toHaveBeenCalled();
    expect(prisma.invoice.update).toHaveBeenCalledWith({
      where: { id: 'inv_1' },
      data: { status: InvoiceStatus.SENT, sentAt: expect.any(Date) },
    });
    expect(prisma.invoiceHistory.create).toHaveBeenCalledWith({
      data: expect.objectContaining({
        description: expect.stringContaining('skipped by user preferences'),
      }),
    });
  });

  it('handles email failure gracefully', async () => {
    const invoice = makeInvoice();
    prisma.invoice.findFirst.mockResolvedValue(invoice);
    pdf.generateInvoicePdf.mockResolvedValue(Buffer.from('PDF'));
    email.sendInvoiceEmail.mockRejectedValue(new Error('SMTP failure'));
    prisma.invoice.update.mockResolvedValue({});
    prisma.invoiceHistory.create.mockResolvedValue({});

    const res = await service.sendInvoice('inv_1', 'user_1');

    expect(res).toEqual({ message: 'Invoice sent successfully' });
    expect(pdf.generateInvoicePdf).toHaveBeenCalled();
    expect(email.sendInvoiceEmail).toHaveBeenCalled();
    expect(prisma.invoice.update).toHaveBeenCalledWith({
      where: { id: 'inv_1' },
      data: { status: InvoiceStatus.SENT, sentAt: expect.any(Date) },
    });
    expect(prisma.invoiceHistory.create).toHaveBeenCalledWith({
      data: expect.objectContaining({
        description: expect.stringContaining('email sending failed'),
      }),
    });
  });

  it('throws NotFoundException when invoice not found', async () => {
    prisma.invoice.findFirst.mockResolvedValue(null);

    await expect(
      service.sendInvoice('missing', 'user_1'),
    ).rejects.toBeInstanceOf(NotFoundException);
    expect(pdf.generateInvoicePdf).not.toHaveBeenCalled();
    expect(email.sendInvoiceEmail).not.toHaveBeenCalled();
    expect(prisma.invoice.update).not.toHaveBeenCalled();
    expect(prisma.invoiceHistory.create).not.toHaveBeenCalled();
  });
});

import {
  Injectable,
  NotFoundException,
  BadRequestException,
} from '@nestjs/common';
import { randomUUID } from 'crypto';
import { PrismaService } from '../../prisma/prisma.service';
import { CreateInvoiceDto } from './dto/create-invoice.dto';
import { UpdateInvoiceDto } from './dto/update-invoice.dto';
import { PdfService } from './pdf.service';
import { InvoiceStatus, HistoryAction, Plan } from '../../common/enums';
import { EmailService } from '../email/email.service';
import { NotificationsService } from '../notifications/notifications.service';

@Injectable()
export class InvoicesService {
  constructor(
    private prisma: PrismaService,
    private pdfService: PdfService,
    private emailService: EmailService,
    private notifications: NotificationsService,
  ) {}

  async create(userId: string, createInvoiceDto: CreateInvoiceDto) {
    // Calculate amounts and sanitize items first
    let subtotal = 0;
    const items = createInvoiceDto.items.map((item) => {
      const amount = item.quantity * item.rate;
      subtotal += amount;
      return {
        description: item.description,
        quantity: item.quantity,
        rate: item.rate,
        amount,
      };
    });

    const taxAmount = createInvoiceDto.taxRate
      ? (subtotal * createInvoiceDto.taxRate) / 100
      : 0;
    const discountAmount = createInvoiceDto.discount
      ? createInvoiceDto.discountType === 'PERCENTAGE'
        ? (subtotal * createInvoiceDto.discount) / 100
        : createInvoiceDto.discount
      : 0;
    const totalAmount = subtotal + taxAmount - discountAmount;

    const invoice = await this.prisma.$transaction(async (tx) => {
      // Enforce plan invoice limits before creating
      const user = await tx.user.findUnique({
        where: { id: userId },
        select: { subscriptionPlan: true, invoiceLimit: true },
      });

      if (user && user.subscriptionPlan !== Plan.ENTERPRISE) {
        if (typeof user.invoiceLimit === 'number' && user.invoiceLimit > 0) {
          const currentCount = await tx.invoice.count({
            where: { userId, status: { not: InvoiceStatus.CANCELLED } },
          });
          if (currentCount >= user.invoiceLimit) {
            throw new BadRequestException(
              'Invoice limit reached for your plan',
            );
          }
        }
      }

      // Determine invoice number: if provided, use it; else atomically increment start number
      let invoiceNumber = createInvoiceDto.invoiceNumber;
      if (!invoiceNumber) {
        const updatedUser = await tx.user.update({
          where: { id: userId },
          data: { invoiceStartNumber: { increment: 1 } },
          select: { invoicePrefix: true, invoiceStartNumber: true },
        });
        const usedNumber = (updatedUser.invoiceStartNumber || 1) - 1;
        invoiceNumber = `${updatedUser.invoicePrefix || 'INV'}-${String(usedNumber).padStart(5, '0')}`;
      }

      const created = await tx.invoice.create({
        data: {
          userId,
          clientId: createInvoiceDto.clientId,
          invoiceNumber,
          invoiceDate: createInvoiceDto.invoiceDate || new Date(),
          dueDate: createInvoiceDto.dueDate,
          status: createInvoiceDto.status || InvoiceStatus.DRAFT,
          subtotal,
          taxRate: createInvoiceDto.taxRate || 0,
          taxAmount,
          discount: createInvoiceDto.discount || 0,
          discountType: createInvoiceDto.discountType || 'FIXED',
          totalAmount,
          balanceDue: totalAmount,
          currency: createInvoiceDto.currency || 'USD',
          notes: createInvoiceDto.notes,
          terms: createInvoiceDto.terms,
          footer: createInvoiceDto.footer,
          items: { create: items },
        },
        include: { client: true, items: true },
      });

      await tx.invoiceHistory.create({
        data: {
          invoiceId: created.id,
          action: HistoryAction.CREATED,
          description: `Invoice ${created.invoiceNumber} created`,
          performedBy: userId,
        },
      });

      return created;
    });

    // Emit event
    try {
      this.notifications.emit(userId, 'invoice.created', {
        id: invoice.id,
        invoiceNumber: invoice.invoiceNumber,
      });
    } catch {}
    return invoice;
  }

  async findAll(
    userId: string,
    query?: {
      status?: (typeof InvoiceStatus)[keyof typeof InvoiceStatus];
      clientId?: string;
      search?: string;
      startDate?: Date;
      endDate?: Date;
      sortBy?: string;
      sortDir?: 'asc' | 'desc';
      page?: number;
      limit?: number;
    },
  ) {
    const where: any = { userId };

    if (query?.status) {
      where.status = query.status;
    }

    if (query?.clientId) {
      where.clientId = query.clientId;
    }

    if (query?.search) {
      where.OR = [
        { invoiceNumber: { contains: query.search, mode: 'insensitive' } },
        { client: { name: { contains: query.search, mode: 'insensitive' } } },
      ];
    }

    if (query?.startDate && query?.endDate) {
      where.invoiceDate = {
        gte: query.startDate,
        lte: query.endDate,
      };
    }

    const rawPage = Number(query?.page);
    const rawLimit = Number(query?.limit);
    const page =
      Number.isFinite(rawPage) && rawPage > 0 ? Math.floor(rawPage) : 1;
    const limit =
      Number.isFinite(rawLimit) && rawLimit > 0
        ? Math.min(100, Math.floor(rawLimit))
        : 10;

    // Sorting
    const allowedSort: Record<string, string> = {
      createdAt: 'createdAt',
      invoiceDate: 'invoiceDate',
      dueDate: 'dueDate',
      totalAmount: 'totalAmount',
      status: 'status',
    };
    const sortBy =
      query?.sortBy && allowedSort[query.sortBy]
        ? allowedSort[query.sortBy]
        : 'createdAt';
    const sortDir: 'asc' | 'desc' =
      query?.sortDir === 'asc' || query?.sortDir === 'desc'
        ? query.sortDir
        : 'desc';

    const [total, itemsRaw] = await Promise.all([
      this.prisma.invoice.count({ where }),
      this.prisma.invoice.findMany({
        where,
        include: {
          client: true,
          payments: true,
          history: {
            where: { action: HistoryAction.CREATED },
            select: { description: true },
            take: 1,
            orderBy: { createdAt: 'asc' },
          },
        },
        orderBy: { [sortBy]: sortDir } as any,
        skip: (page - 1) * limit,
        take: limit,
      }),
    ]);

    const items = itemsRaw.map((inv: any) => {
      const createdDesc: string | undefined = inv?.history?.[0]?.description;
      const generatedFromRecurring = !!(
        createdDesc && createdDesc.includes('created from recurring template')
      );
      return { ...inv, generatedFromRecurring };
    });

    return {
      items,
      total,
      page,
      limit,
      totalPages: Math.ceil(total / limit),
    };
  }

  async findOne(id: string, userId: string) {
    const invoiceRaw = await this.prisma.invoice.findFirst({
      where: { id, userId },
      include: {
        client: true,
        items: true,
        payments: true,
        history: {
          orderBy: { createdAt: 'desc' },
        },
      },
    });

    if (!invoiceRaw) {
      throw new NotFoundException('Invoice not found');
    }

    const createdEntry = invoiceRaw.history?.find(
      (h) =>
        typeof (h as any).description === 'string' &&
        (h as any).description.includes('created from recurring template'),
    );
    const generatedFromRecurring = !!createdEntry;
    return { ...invoiceRaw, generatedFromRecurring } as any;
  }

  async update(id: string, userId: string, updateInvoiceDto: UpdateInvoiceDto) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Recalculate amounts if items are updated
    let updateData: any = { ...updateInvoiceDto };
    // Remove fields that do not exist in Prisma schema
    if ('recurring' in updateData) delete updateData.recurring;
    if ('recurringInterval' in updateData) delete updateData.recurringInterval;

    if ((updateInvoiceDto as any).items) {
      let subtotal = 0;
      const items = (updateInvoiceDto as any).items.map((item: any) => {
        const amount = item.quantity * item.rate;
        subtotal += amount;

        return {
          description: item.description,
          quantity: item.quantity,
          rate: item.rate,
          amount,
        };
      });

      const taxAmount = (updateInvoiceDto as any).taxRate
        ? (subtotal * (updateInvoiceDto as any).taxRate) / 100
        : 0;

      const discountAmount = (updateInvoiceDto as any).discount
        ? (updateInvoiceDto as any).discountType === 'PERCENTAGE'
          ? (subtotal * (updateInvoiceDto as any).discount) / 100
          : (updateInvoiceDto as any).discount
        : 0;

      const totalAmount = subtotal + taxAmount - discountAmount;

      updateData = {
        ...updateData,
        subtotal,
        taxAmount,
        totalAmount,
        balanceDue: totalAmount - invoice.paidAmount,
      };

      // Delete existing items and create new ones
      await this.prisma.invoiceItem.deleteMany({
        where: { invoiceId: id },
      });

      await this.prisma.invoiceItem.createMany({
        data: items.map((item) => ({ ...item, invoiceId: id })),
      });

      delete updateData.items;
    }

    const updatedInvoice = await this.prisma.invoice.update({
      where: { id },
      data: updateData,
      include: {
        client: true,
        items: true,
      },
    });

    // Create history entry
    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: id,
        action: HistoryAction.UPDATED,
        description: 'Invoice updated',
        performedBy: userId,
      },
    });

    try {
      this.notifications.emit(userId, 'invoice.updated', {
        id,
        invoiceNumber: updatedInvoice.invoiceNumber,
      });
    } catch {}
    return updatedInvoice;
  }

  async cancelInvoice(id: string, userId: string) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    if (invoice.status === InvoiceStatus.PAID) {
      throw new BadRequestException('Cannot cancel a paid invoice');
    }

    if (invoice.status === InvoiceStatus.CANCELLED) {
      return invoice;
    }

    const updated = await this.prisma.invoice.update({
      where: { id },
      data: {
        status: InvoiceStatus.CANCELLED,
        cancelledAt: new Date(),
      },
    });

    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: id,
        action: HistoryAction.CANCELLED,
        description: 'Invoice cancelled',
        performedBy: userId,
      },
    });

    try {
      this.notifications.emit(userId, 'invoice.cancelled', { id });
    } catch {}
    return updated;
  }

  async updateShare(
    id: string,
    userId: string,
    opts: { enable?: boolean; regenerate?: boolean },
  ) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
    });
    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }
    const data: any = {};
    if (typeof opts.enable === 'boolean') data.shareEnabled = opts.enable;
    if (opts.regenerate) data.shareId = randomUUID();
    const updated = await this.prisma.invoice.update({
      where: { id },
      data: data,
      select: { id: true, shareId: true, shareEnabled: true },
    });
    try {
      this.notifications.emit(userId, 'invoice.share_updated', {
        id,
        shareId: updated.shareId,
        shareEnabled: updated.shareEnabled,
      });
    } catch {}
    return updated;
  }

  async remove(id: string, userId: string) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Check if invoice has payments
    const paymentCount = await this.prisma.payment.count({
      where: { invoiceId: id },
    });

    if (paymentCount > 0) {
      throw new BadRequestException('Cannot delete invoice with payments');
    }

    await this.prisma.invoice.delete({
      where: { id },
    });

    return { message: 'Invoice deleted successfully' };
  }

  async updateStatus(
    id: string,
    userId: string,
    status: (typeof InvoiceStatus)[keyof typeof InvoiceStatus],
  ) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    const data: any = { status };
    if (status === InvoiceStatus.CANCELLED) {
      data.cancelledAt = new Date();
    }
    if (status === InvoiceStatus.PAID) {
      data.paidAt = new Date();
    }

    const updatedInvoice = await this.prisma.invoice.update({
      where: { id },
      data,
    });

    // Create history entry
    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: id,
        action: HistoryAction.STATUS_CHANGED,
        description: `Status changed to ${status}`,
        performedBy: userId,
      },
    });

    try {
      this.notifications.emit(userId, 'invoice.status_changed', { id, status });
    } catch {}
    return updatedInvoice;
  }

  async sendInvoice(id: string, userId: string) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
      include: {
        client: true,
        items: true,
        user: true,
      },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Respect user email preferences; attempt to send email if enabled
    const shouldSendEmail = !!(
      invoice.user?.emailNotificationsEnabled &&
      invoice.user?.emailNotifyNewInvoice
    );

    let emailSent = false;
    let emailSkipOrFailDescription: string | undefined;
    if (shouldSendEmail) {
      try {
        // Ensure public share link is enabled so the client can view & pay
        let invForEmail = invoice;
        if (!invoice.shareEnabled || !invoice.shareId) {
          invForEmail = await this.prisma.invoice.update({
            where: { id },
            data: {
              shareEnabled: true,
              shareId: invoice.shareId || randomUUID(),
            },
            include: { client: true, items: true, user: true },
          });
        }

        const pdfBuffer = await this.pdfService.generateInvoicePdf(invForEmail);
        await this.emailService.sendInvoiceEmail(invForEmail as any, pdfBuffer);
        emailSent = true;
      } catch (err) {
        // Log and continue without failing the request
        console.error('Failed to send invoice email:', err);
        emailSkipOrFailDescription =
          'Invoice marked as SENT (email sending failed)';
      }
    } else {
      emailSkipOrFailDescription =
        'Invoice marked as SENT (email sending skipped by user preferences)';
    }

    // Update status to SENT
    await this.prisma.invoice.update({
      where: { id },
      data: {
        status: InvoiceStatus.SENT,
        sentAt: new Date(),
      },
    });

    // Create history entry
    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: id,
        action: HistoryAction.SENT,
        description: emailSent
          ? `Invoice sent to ${invoice.client.email}`
          : (emailSkipOrFailDescription as string),
        performedBy: userId,
      },
    });

    try {
      this.notifications.emit(userId, 'invoice.sent', {
        id,
        emailSent: !!emailSent,
      });
    } catch {}
    return { message: 'Invoice sent successfully' };
  }

  async sendInvoicesBulk(ids: string[], userId: string) {
    if (!Array.isArray(ids) || ids.length === 0) {
      throw new BadRequestException('ids array is required');
    }

    // Fetch all invoices belonging to user
    const invoices = await this.prisma.invoice.findMany({
      where: { id: { in: ids }, userId },
      include: { client: true, items: true, user: true },
    });

    const foundIds = new Set(invoices.map((i) => i.id));
    const missingIds = ids.filter((id) => !foundIds.has(id));

    const results: Array<{
      id: string;
      status: 'sent' | 'skipped' | 'failed' | 'not_found';
      message?: string;
    }> = [];

    for (const id of ids) {
      const inv = invoices.find((i) => i.id === id);
      if (!inv) {
        results.push({
          id,
          status: 'not_found',
          message: 'Invoice not found or not owned by user',
        });
        continue;
      }

      // Respect user email preferences
      const shouldSendEmail = !!(
        inv.user?.emailNotificationsEnabled && inv.user?.emailNotifyNewInvoice
      );
      if (!shouldSendEmail) {
        try {
          // Still mark as SENT (skipped email)
          await this.prisma.invoice.update({
            where: { id: inv.id },
            data: { status: InvoiceStatus.SENT, sentAt: new Date() },
          });
          await this.prisma.invoiceHistory.create({
            data: {
              invoiceId: inv.id,
              action: HistoryAction.SENT,
              description:
                'Invoice marked as SENT (email sending skipped by user preferences)',
              performedBy: userId,
            },
          });
          results.push({
            id: inv.id,
            status: 'skipped',
            message: 'Email disabled by user preferences',
          });
        } catch (e: any) {
          results.push({
            id: inv.id,
            status: 'failed',
            message: e?.message || 'Failed to update invoice status',
          });
        }
        continue;
      }

      try {
        const pdfBuffer = await this.pdfService.generateInvoicePdf(inv as any);
        await this.emailService.sendInvoiceEmail(inv as any, pdfBuffer);
        await this.prisma.invoice.update({
          where: { id: inv.id },
          data: { status: InvoiceStatus.SENT, sentAt: new Date() },
        });
        await this.prisma.invoiceHistory.create({
          data: {
            invoiceId: inv.id,
            action: HistoryAction.SENT,
            description: `Invoice sent to ${inv.client.email}`,
            performedBy: userId,
          },
        });
        results.push({ id: inv.id, status: 'sent' });
      } catch (e: any) {
        // Log and continue

        console.error('Failed to send invoice email (bulk):', e);
        try {
          await this.prisma.invoice.update({
            where: { id: inv.id },
            data: { status: InvoiceStatus.SENT, sentAt: new Date() },
          });
          await this.prisma.invoiceHistory.create({
            data: {
              invoiceId: inv.id,
              action: HistoryAction.SENT,
              description:
                'Invoice marked as SENT (email sending failed in bulk)',
              performedBy: userId,
            },
          });
        } catch {}
        results.push({
          id: inv.id,
          status: 'failed',
          message: e?.message || 'Email sending failed',
        });
      }
    }

    const summary = {
      totalRequested: ids.length,
      sent: results.filter((r) => r.status === 'sent').length,
      skipped: results.filter((r) => r.status === 'skipped').length,
      failed: results.filter((r) => r.status === 'failed').length,
      notFound: results.filter((r) => r.status === 'not_found').length,
    } as const;

    return { summary, results };
  }

  async updateStatusBulk(
    ids: string[],
    userId: string,
    status: (typeof InvoiceStatus)[keyof typeof InvoiceStatus],
  ) {
    if (!Array.isArray(ids) || ids.length === 0) {
      throw new BadRequestException('ids array is required');
    }
    const invoices = await this.prisma.invoice.findMany({
      where: { id: { in: ids }, userId },
    });
    const foundIds = invoices.map((i) => i.id);
    if (foundIds.length === 0) {
      return {
        summary: {
          totalRequested: ids.length,
          updated: 0,
          notFound: ids.length,
        },
      };
    }

    const data: any = { status };
    if (status === InvoiceStatus.CANCELLED) data.cancelledAt = new Date();
    if (status === InvoiceStatus.PAID) data.paidAt = new Date();

    await this.prisma.invoice.updateMany({
      where: { id: { in: foundIds } },
      data,
    });

    // Write history entries
    try {
      await this.prisma.invoiceHistory.createMany({
        data: foundIds.map((id) => ({
          invoiceId: id,
          action: HistoryAction.STATUS_CHANGED,
          description: `Status changed to ${status} (bulk)`,
          performedBy: userId,
        })),
      });
    } catch {}

    // Emit events
    try {
      for (const id of foundIds) {
        this.notifications.emit(userId, 'invoice.status_changed', {
          id,
          status,
        });
      }
    } catch {}

    return {
      summary: {
        totalRequested: ids.length,
        updated: foundIds.length,
        notFound: ids.length - foundIds.length,
      },
    };
  }

  async markPaidBulk(ids: string[], userId: string) {
    if (!Array.isArray(ids) || ids.length === 0) {
      throw new BadRequestException('ids array is required');
    }
    const invoices = await this.prisma.invoice.findMany({
      where: { id: { in: ids }, userId },
    });
    const foundIds = invoices.map((i) => i.id);
    if (foundIds.length === 0) {
      return {
        summary: {
          totalRequested: ids.length,
          updated: 0,
          notFound: ids.length,
        },
      };
    }

    const now = new Date();
    await this.prisma.$transaction([
      ...invoices.map((inv) =>
        this.prisma.invoice.update({
          where: { id: inv.id },
          data: {
            status: InvoiceStatus.PAID,
            paidAt: now,
            paidAmount: inv.totalAmount,
            balanceDue: 0,
          },
        }),
      ),
      this.prisma.invoiceHistory.createMany({
        data: foundIds.map((id) => ({
          invoiceId: id,
          action: HistoryAction.PAYMENT_RECEIVED,
          description: 'Marked as paid (bulk)',
          performedBy: userId,
        })),
      }),
    ]);

    // Emit events
    try {
      for (const id of foundIds) {
        this.notifications.emit(userId, 'invoice.status_changed', {
          id,
          status: InvoiceStatus.PAID,
        });
      }
    } catch {}

    return {
      summary: {
        totalRequested: ids.length,
        updated: foundIds.length,
        notFound: ids.length - foundIds.length,
      },
    };
  }

  async removeBulk(ids: string[], userId: string) {
    if (!Array.isArray(ids) || ids.length === 0) {
      throw new BadRequestException('ids array is required');
    }
    const invoices = await this.prisma.invoice.findMany({
      where: { id: { in: ids }, userId },
      select: { id: true },
    });
    const foundIds = invoices.map((i) => i.id);
    if (foundIds.length === 0) {
      return {
        summary: {
          totalRequested: ids.length,
          deleted: 0,
          skipped: 0,
          notFound: ids.length,
        },
      };
    }

    const deletable: string[] = [];
    for (const id of foundIds) {
      const count = await this.prisma.payment.count({
        where: { invoiceId: id },
      });
      if (count === 0) deletable.push(id);
    }

    if (deletable.length > 0) {
      await this.prisma.invoice.deleteMany({
        where: { id: { in: deletable }, userId },
      });
    }

    // Emit events
    try {
      for (const id of deletable) {
        this.notifications.emit(userId, 'invoice.deleted', { id });
      }
    } catch {}

    return {
      summary: {
        totalRequested: ids.length,
        deleted: deletable.length,
        skipped: foundIds.length - deletable.length,
        notFound: ids.length - foundIds.length,
      },
    };
  }

  async downloadPdf(
    id: string,
    userId: string,
    options?: {
      template?: string;
      colorScheme?: string;
      font?: string;
      layout?: string;
      footer?: string;
    },
  ): Promise<Buffer> {
    const invoice = await this.prisma.invoice.findFirst({
      where: { id, userId },
      include: {
        client: true,
        items: true,
        user: true,
      },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Create history entry (non-blocking). If enum value is missing in DB, don't fail the request.
    try {
      await this.prisma.invoiceHistory.create({
        data: {
          invoiceId: id,
          action: HistoryAction.EXPORTED,
          description: 'Invoice PDF downloaded',
          performedBy: userId,
        },
      });
    } catch (e: any) {
      console.error(
        'Failed to write invoice history (EXPORTED):',
        e?.message || e,
      );
    }

    const mergedOptions = {
      ...(options || {}),
      footer:
        options?.footer ??
        invoice.footer ??
        (invoice as any)?.user?.invoiceFooter,
    };

    return this.pdfService.generateInvoicePdf(invoice, mergedOptions as any);
  }

  async duplicateInvoice(id: string, userId: string) {
    return this.prisma.$transaction(async (tx) => {
      const invoice = await tx.invoice.findFirst({
        where: { id, userId },
        include: { items: true },
      });

      if (!invoice) {
        throw new NotFoundException('Invoice not found');
      }

      // Enforce plan invoice limits before duplicating
      const user = await tx.user.findUnique({
        where: { id: userId },
        select: { subscriptionPlan: true, invoiceLimit: true },
      });

      if (user && user.subscriptionPlan !== Plan.ENTERPRISE) {
        if (typeof user.invoiceLimit === 'number' && user.invoiceLimit > 0) {
          const currentCount = await tx.invoice.count({
            where: { userId, status: { not: InvoiceStatus.CANCELLED } },
          });
          if (currentCount >= user.invoiceLimit) {
            throw new BadRequestException(
              'Invoice limit reached for your plan',
            );
          }
        }
      }

      // Atomically increment and use previous number
      const updatedUser = await tx.user.update({
        where: { id: userId },
        data: { invoiceStartNumber: { increment: 1 } },
        select: { invoicePrefix: true, invoiceStartNumber: true },
      });
      const usedNumber = (updatedUser.invoiceStartNumber || 1) - 1;
      const invoiceNumber = `${updatedUser.invoicePrefix || 'INV'}-${String(usedNumber).padStart(5, '0')}`;

      const duplicate = await tx.invoice.create({
        data: {
          userId,
          clientId: invoice.clientId,
          invoiceNumber,
          invoiceDate: new Date(),
          dueDate: new Date(
            Date.now() +
              (invoice.dueDate.getTime() - invoice.invoiceDate.getTime()),
          ),
          status: InvoiceStatus.DRAFT,
          subtotal: invoice.subtotal,
          taxRate: invoice.taxRate,
          taxAmount: invoice.taxAmount,
          discount: invoice.discount,
          discountType: invoice.discountType,
          totalAmount: invoice.totalAmount,
          balanceDue: invoice.totalAmount - invoice.paidAmount,
          currency: invoice.currency,
          notes: invoice.notes,
          terms: invoice.terms,
          footer: invoice.footer,
          items: {
            create: invoice.items.map((item) => ({
              description: item.description,
              quantity: item.quantity,
              rate: item.rate,
              amount: item.amount,
            })),
          },
        },
        include: { client: true, items: true },
      });

      await tx.invoiceHistory.create({
        data: {
          invoiceId: duplicate.id,
          action: HistoryAction.CREATED,
          description: `Invoice duplicated from ${invoice.invoiceNumber}`,
          performedBy: userId,
        },
      });
      try {
        this.notifications.emit(userId, 'invoice.duplicated', {
          id: duplicate.id,
          from: invoice.id,
        });
      } catch {}
      return duplicate;
    });
  }

  async getStatistics(userId: string) {
    const [total, paid, overdue, draft] = await Promise.all([
      this.prisma.invoice.count({ where: { userId } }),
      this.prisma.invoice.count({
        where: { userId, status: InvoiceStatus.PAID },
      }),
      this.prisma.invoice.count({
        where: {
          userId,
          status: InvoiceStatus.OVERDUE,
        },
      }),
      this.prisma.invoice.count({
        where: { userId, status: InvoiceStatus.DRAFT },
      }),
    ]);

    const totalRevenue = await this.prisma.invoice.aggregate({
      where: { userId, status: InvoiceStatus.PAID },
      _sum: { totalAmount: true },
    });

    const pendingAmount = await this.prisma.invoice.aggregate({
      where: {
        userId,
        status: {
          in: [InvoiceStatus.SENT, InvoiceStatus.VIEWED, InvoiceStatus.OVERDUE],
        },
      },
      _sum: { totalAmount: true },
    });

    return {
      total,
      paid,
      overdue,
      draft,
      totalRevenue: totalRevenue._sum.totalAmount || 0,
      pendingAmount: pendingAmount._sum.totalAmount || 0,
    };
  }

  async getPublicInvoiceByShareId(
    shareId: string,
    metadata?: { ip?: string; userAgent?: string },
  ) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { shareId, shareEnabled: true },
      include: {
        client: true,
        items: true,
        payments: true,
        user: true,
        history: { orderBy: { createdAt: 'desc' } },
      },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Only mark viewed if not cancelled/paid
    const shouldMarkViewed =
      invoice.status !== InvoiceStatus.CANCELLED &&
      invoice.status !== InvoiceStatus.PAID;

    if (shouldMarkViewed) {
      const newStatus =
        invoice.status === InvoiceStatus.SENT ||
        invoice.status === InvoiceStatus.DRAFT
          ? InvoiceStatus.VIEWED
          : invoice.status;

      await this.prisma.$transaction(async (tx) => {
        await tx.invoice.update({
          where: { id: invoice.id },
          data: {
            viewedAt: new Date(),
            status: newStatus,
          },
        });

        await tx.invoiceHistory.create({
          data: {
            invoiceId: invoice.id,
            action: HistoryAction.VIEWED,
            description: 'Invoice viewed via share link',
            metadata: metadata ? (metadata as any) : undefined,
            performedBy: null,
          },
        });
      });

      // Return the fresh copy after updates
      const updated = await this.prisma.invoice.findUnique({
        where: { id: invoice.id },
        include: {
          client: true,
          items: true,
          payments: true,
          user: true,
          history: { orderBy: { createdAt: 'desc' } },
        },
      });
      try {
        this.notifications.emit(invoice.userId, 'invoice.viewed', {
          id: invoice.id,
        });
      } catch {}
      return updated!;
    }

    return invoice;
  }
}

import { Injectable } from '@nestjs/common';

@Injectable()
export class PdfService {
  async generateInvoicePdf(
    invoice: any,
    options?: {
      color?: string;
      font?: string;
      layout?: string;
      footer?: string;
      pageSize?: string;
      margins?: string;
      watermarkText?: string;
      showSignature?: boolean;
      customFields?: Record<string, string>;
      logoSize?: string;
      companyName?: string;
      companyAddress?: string;
      companyPhone?: string;
      companyEmail?: string;
      headerBorderColor?: string;
      tableHeaderColor?: string;
      accentColor?: string;
    },
  ): Promise<Buffer> {
    try {
      // Import pdf-lib
      let mod: any;
      try {
        mod = await import('pdf-lib/dist/pdf-lib.js');
      } catch {
        try {
          mod = await import('pdf-lib/cjs/index.js');
        } catch {
