        case 'letter':
          pageWidth = 612;
          pageHeight = 792;
          break;
        case 'legal':
          pageWidth = 612;
          pageHeight = 1008;
          break;
        case 'a3':
          pageWidth = 842;
          pageHeight = 1191;
          break;
        case 'a5':
          pageWidth = 420;
          pageHeight = 595;
          break;
        default:
          pageWidth = 595;
          pageHeight = 842; // A4
      }

