import { Controller, Get, Param, Req } from '@nestjs/common';
import type { Request } from 'express';
import { InvoicesService } from './invoices.service';

@Controller('public/invoices')
export class PublicInvoicesController {
  constructor(private readonly invoicesService: InvoicesService) {}

  @Get(':shareId')
  getByShareId(@Param('shareId') shareId: string, @Req() req: Request) {
    const ipHeader = (req.headers['x-forwarded-for'] as string) || req.ip;
    const ip = Array.isArray(ipHeader)
      ? ipHeader[0]
      : ipHeader?.split(',')[0] || req.ip;
    const userAgent = req.headers['user-agent'];
    return this.invoicesService.getPublicInvoiceByShareId(shareId, {
      ip,
      userAgent,
    });
  }
}

import { Controller, Get, Query, Sse } from '@nestjs/common';
import { NotificationsService } from './notifications.service';
import { Observable } from 'rxjs';
import { MessageEvent } from '@nestjs/common';

@Controller('notifications')
export class NotificationsController {
  constructor(private readonly notifications: NotificationsService) {}

  // Server-Sent Events stream: client must pass a JWT token (same as API token)
  @Sse('stream')
  stream(@Query('token') token?: string): Observable<MessageEvent> {
    const payload = this.notifications.verifyToken(token);
    const userId = payload?.sub;
    return this.notifications.stream(userId);
  }
}

