    emailNotifyPaymentReceived: true,
  },
  ...overrides,
});

const makePayment = (overrides: Partial<any> = {}) => ({
  id: 'pay_1',
  invoiceId: 'inv_1',
  amount: 100,
  status: PaymentStatus.COMPLETED,
  paymentDate: new Date(),
  paymentNumber: 'PMT-1',
  netAmount: 100,
  ...overrides,
});

describe('PaymentsService', () => {
  let service: PaymentsService;
  let prisma: jest.Mocked<any>;
  let email: jest.Mocked<EmailService>;
  const realRandom = Math.random;

  beforeEach(async () => {
    prisma = {
      invoice: {
        findFirst: jest.fn(),
        update: jest.fn(),
      },
      payment: {
        create: jest.fn(),
        update: jest.fn(),
      },
      invoiceHistory: {
        create: jest.fn(),
      },
    } as any;

    email = {
      sendPaymentReceivedEmail: jest.fn(),
      // PaymentsService also calls this when emailNotificationsEnabled is true
      sendClientPaymentReceiptEmail: jest.fn(),
    } as any;

    const moduleRef: TestingModule = await Test.createTestingModule({
      providers: [
        PaymentsService,
        { provide: PrismaService, useValue: prisma },
        { provide: EmailService, useValue: email },
