import {
  Injectable,
  NotFoundException,
  BadRequestException,
} from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import { CreatePaymentDto, ProcessPaymentDto } from './dto/create-payment.dto';
import {
  PaymentStatus,
  InvoiceStatus,
  HistoryAction,
} from '../../common/enums';
import { EmailService } from '../email/email.service';
import { NotificationsService } from '../notifications/notifications.service';

@Injectable()
export class PaymentsService {
  constructor(
    private prisma: PrismaService,
    private emailService: EmailService,
    private notifications: NotificationsService,
  ) {}

  async create(userId: string, createPaymentDto: CreatePaymentDto) {
    // Verify invoice exists and belongs to user
    const invoice = await this.prisma.invoice.findFirst({
      where: {
        id: createPaymentDto.invoiceId,
        userId,
      },
      include: {
        payments: true,
        client: true,
        user: true,
      },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Calculate total paid amount
    const totalPaid = invoice.payments.reduce(
      (sum, payment) =>
        sum + (payment.status === PaymentStatus.COMPLETED ? payment.amount : 0),
      0,
    );

    const remainingAmount = invoice.totalAmount - totalPaid;

    if (createPaymentDto.amount > remainingAmount) {
      throw new BadRequestException('Payment amount exceeds invoice balance');
    }

    // Create payment
    const payment = await this.prisma.payment.create({
      data: {
        ...createPaymentDto,
        status: PaymentStatus.COMPLETED,
        paymentDate: createPaymentDto.paymentDate || new Date(),
        paymentNumber: `PMT-${Date.now()}`,
        netAmount: createPaymentDto.amount,
      },
    });

    // Update invoice status if fully paid
    const newTotalPaid = totalPaid + createPaymentDto.amount;
    if (newTotalPaid >= invoice.totalAmount) {
      await this.prisma.invoice.update({
        where: { id: createPaymentDto.invoiceId },
        data: {
          status: InvoiceStatus.PAID,
          paidAmount: newTotalPaid,
          balanceDue: 0,
          paidAt: new Date(),
        },
      });

      // Create history entry
      await this.prisma.invoiceHistory.create({
        data: {
          invoiceId: createPaymentDto.invoiceId,
          action: HistoryAction.PAYMENT_RECEIVED,
          description: `Invoice fully paid`,
          performedBy: userId,
        },
      });
    } else {
      await this.prisma.invoice.update({
        where: { id: createPaymentDto.invoiceId },
        data: {
          status: InvoiceStatus.PARTIALLY_PAID,
          paidAmount: newTotalPaid,
          balanceDue: invoice.totalAmount - newTotalPaid,
        },
      });

      // Create history entry
      await this.prisma.invoiceHistory.create({
        data: {
          invoiceId: createPaymentDto.invoiceId,
          action: HistoryAction.PAYMENT_RECEIVED,
          description: `Partial payment of ${createPaymentDto.amount} received`,
          performedBy: userId,
        },
      });
    }

    // Send payment received email to the user if preferences allow
    if (
      invoice.user?.emailNotificationsEnabled &&
      invoice.user?.emailNotifyPaymentReceived
    ) {
      try {
        await this.emailService.sendPaymentReceivedEmail(
          invoice.user as any,
          invoice.client as any,
          invoice as any,
          createPaymentDto.amount,
        );
      } catch (err) {
        console.error('Failed to send payment received email (create):', err);
      }
    }

    // Send receipt email to the client (respect global emailNotificationsEnabled)
    if (invoice.user?.emailNotificationsEnabled) {
      try {
        const updatedPaidAmount = newTotalPaid;
        const updatedBalance = Math.max(
          0,
          (invoice.totalAmount || 0) - updatedPaidAmount,
        );
        await this.emailService.sendClientPaymentReceiptEmail(
          invoice.client as any,
          {
            ...invoice,
            paidAmount: updatedPaidAmount,
            balanceDue: updatedBalance,
          } as any,
          createPaymentDto.amount,
          invoice.user as any,
        );
      } catch (err) {
        console.error(
          'Failed to send client payment receipt email (create):',
          err,
        );
      }
    }

    try {
      this.notifications.emit(userId, 'payment.recorded', {
        invoiceId: createPaymentDto.invoiceId,
        amount: createPaymentDto.amount,
        paymentId: payment.id,
      });
    } catch {}
    return payment;
  }

  async processPayment(userId: string, processPaymentDto: ProcessPaymentDto) {
    // Verify invoice exists and belongs to user
    const invoice = await this.prisma.invoice.findFirst({
      where: {
        id: processPaymentDto.invoiceId,
        userId,
      },
      include: {
        payments: true,
        client: true,
        user: true,
      },
    });

    if (!invoice) {
      throw new NotFoundException('Invoice not found');
    }

    // Mock payment processing
    // In a real application, this would integrate with payment gateways
    const transactionId = `TXN-${Date.now()}-${Math.random().toString(36).substr(2, 9).toUpperCase()}`;

    // Simulate payment processing delay
    await new Promise((resolve) => setTimeout(resolve, 1000));

    // Mock success/failure (90% success rate for demo)
    const isSuccess = Math.random() > 0.1;

    if (!isSuccess) {
      // Create failed payment record
      const failedPayment = await this.prisma.payment.create({
        data: {
          invoiceId: processPaymentDto.invoiceId,
          amount: processPaymentDto.amount,
          paymentMethod: processPaymentDto.paymentMethod,
          paymentDate: new Date(),
          transactionId,
          status: PaymentStatus.FAILED,
          notes: 'Payment processing failed',
          paymentNumber: `PMT-${Date.now()}`,
          netAmount: 0,
        },
      });

      throw new BadRequestException(
        'Payment processing failed. Please try again.',
      );
    }

    // Create successful payment
    const payment = await this.prisma.payment.create({
      data: {
        invoiceId: processPaymentDto.invoiceId,
        amount: processPaymentDto.amount,
        paymentMethod: processPaymentDto.paymentMethod,
        paymentDate: new Date(),
        transactionId,
        status: PaymentStatus.COMPLETED,
        notes: processPaymentDto.notes,
        paymentNumber: `PMT-${Date.now()}`,
        netAmount: processPaymentDto.amount,
      },
    });

    // Calculate total paid amount
    const totalPaid =
      invoice.payments.reduce(
        (sum, p) => sum + (p.status === PaymentStatus.COMPLETED ? p.amount : 0),
        0,
      ) + processPaymentDto.amount;

    // Update invoice status
    if (totalPaid >= invoice.totalAmount) {
      await this.prisma.invoice.update({
        where: { id: processPaymentDto.invoiceId },
        data: {
          status: InvoiceStatus.PAID,
          paidAmount: totalPaid,
          balanceDue: 0,
          paidAt: new Date(),
        },
      });

      // Create history entry
      await this.prisma.invoiceHistory.create({
        data: {
          invoiceId: processPaymentDto.invoiceId,
          action: HistoryAction.PAYMENT_RECEIVED,
          description: `Invoice paid via ${processPaymentDto.paymentMethod}`,
          performedBy: userId,
        },
      });
    } else {
      await this.prisma.invoice.update({
        where: { id: processPaymentDto.invoiceId },
        data: {
          status: InvoiceStatus.PARTIALLY_PAID,
          paidAmount: totalPaid,
          balanceDue: invoice.totalAmount - totalPaid,
        },
      });

      // Create history entry
      await this.prisma.invoiceHistory.create({
        data: {
          invoiceId: processPaymentDto.invoiceId,
          action: HistoryAction.PAYMENT_RECEIVED,
          description: `Partial payment of ${processPaymentDto.amount} via ${processPaymentDto.paymentMethod}`,
          performedBy: userId,
        },
      });
    }

    // Send payment received email to the user if preferences allow
    if (
      invoice.user?.emailNotificationsEnabled &&
      invoice.user?.emailNotifyPaymentReceived
    ) {
      try {
        await this.emailService.sendPaymentReceivedEmail(
          invoice.user as any,
          invoice.client as any,
          invoice as any,
          processPaymentDto.amount,
        );
      } catch (err) {
        console.error(
          'Failed to send payment received email (processPayment):',
          err,
        );
      }
    }

    // Send receipt email to the client (respect global emailNotificationsEnabled)
    if (invoice.user?.emailNotificationsEnabled) {
      try {
        const updatedBalance = Math.max(
          0,
          (invoice.totalAmount || 0) - totalPaid,
        );
        await this.emailService.sendClientPaymentReceiptEmail(
          invoice.client as any,
          {
            ...invoice,
            paidAmount: totalPaid,
            balanceDue: updatedBalance,
          } as any,
          processPaymentDto.amount,
          invoice.user as any,
        );
      } catch (err) {
        console.error(
          'Failed to send client payment receipt email (processPayment):',
          err,
        );
      }
    }

    try {
      this.notifications.emit(userId, 'payment.processed', {
        invoiceId: processPaymentDto.invoiceId,
        amount: processPaymentDto.amount,
        paymentId: payment.id,
        transactionId,
      });
    } catch {}
    return {
      payment,
      message: 'Payment processed successfully',
      transactionId,
    };
  }

  async findAll(
    userId: string,
    query?: {
      invoiceId?: string;
      status?: PaymentStatus;
      method?: any;
      dateFrom?: string;
      dateTo?: string;
      search?: string;
      page?: number;
      limit?: number;
    },
  ) {
    const where: any = {};

    if (query?.invoiceId) {
      where.invoiceId = query.invoiceId;
    }

    if (query?.status) {
      where.status = query.status;
    }

    if (query?.method) {
      where.paymentMethod = query.method;
    }

    if (query?.dateFrom || query?.dateTo) {
      where.paymentDate = {};
      if (query.dateFrom) {
        const from = new Date(query.dateFrom);
        from.setHours(0, 0, 0, 0);
        where.paymentDate.gte = from;
      }
      if (query.dateTo) {
        const to = new Date(query.dateTo);
        to.setHours(23, 59, 59, 999);
        where.paymentDate.lte = to;
      }
    }

    if (query?.search) {
      const s = String(query.search).trim();
      if (s.length > 0) {
        where.OR = [
          { transactionId: { contains: s, mode: 'insensitive' } },
          { paymentNumber: { contains: s, mode: 'insensitive' } },
          { notes: { contains: s, mode: 'insensitive' } },
          { invoice: { invoiceNumber: { contains: s, mode: 'insensitive' } } },
          {
            invoice: { client: { name: { contains: s, mode: 'insensitive' } } },
          },
        ];
      }
    }

    // Verify user owns the invoices
    where.invoice = { userId };

    const include = {
      invoice: {
        include: {
          client: true,
        },
      },
    } as const;

    const orderBy = { paymentDate: 'desc' } as const;

    if (query?.page && query?.limit) {
      const page = Math.max(1, Number(query.page) || 1);
      const limit = Math.max(1, Math.min(100, Number(query.limit) || 10));
      const skip = (page - 1) * limit;
      const [total, items] = await Promise.all([
        this.prisma.payment.count({ where }),
        this.prisma.payment.findMany({
          where,
          include,
          orderBy,
          skip,
          take: limit,
        }),
      ]);
      const totalPages = Math.max(1, Math.ceil(total / limit));
      return { items, total, totalPages, page, limit };
    }

    return this.prisma.payment.findMany({ where, include, orderBy });
  }

  async findOne(id: string, userId: string) {
    const payment = await this.prisma.payment.findUnique({
      where: { id },
      include: {
        invoice: {
          include: {
            client: true,
          },
        },
      },
    });

    if (!payment) {
      throw new NotFoundException('Payment not found');
    }

    // Verify user owns the invoice
    if (payment.invoice.userId !== userId) {
      throw new NotFoundException('Payment not found');
    }

    return payment;
  }

  async refund(id: string, userId: string, amount?: number) {
    const payment = await this.prisma.payment.findUnique({
      where: { id },
      include: {
        invoice: true,
      },
    });

    if (!payment) {
      throw new NotFoundException('Payment not found');
    }

    // Verify user owns the invoice
    if (payment.invoice.userId !== userId) {
      throw new NotFoundException('Payment not found');
    }

    if (payment.status !== PaymentStatus.COMPLETED) {
      throw new BadRequestException('Can only refund completed payments');
    }

    const refundAmount = amount || payment.amount;

    if (refundAmount > payment.amount) {
      throw new BadRequestException('Refund amount exceeds payment amount');
    }

    // Create refund payment record
    const refund = await this.prisma.payment.create({
      data: {
        invoiceId: payment.invoiceId,
        amount: -refundAmount,
        paymentMethod: payment.paymentMethod,
        paymentDate: new Date(),
        transactionId: `REFUND-${payment.transactionId}`,
        status: PaymentStatus.REFUNDED,
        notes: `Refund for payment ${payment.transactionId}`,
        paymentNumber: `PMT-${Date.now()}`,
        netAmount: -refundAmount,
      },
    });

    // Update original payment status if fully refunded
    if (refundAmount === payment.amount) {
      await this.prisma.payment.update({
        where: { id },
        data: { status: PaymentStatus.REFUNDED },
      });
    }

    // Update invoice paid amount
    const updatedInvoice = await this.prisma.invoice.update({
      where: { id: payment.invoiceId },
      data: {
        paidAmount: {
          decrement: refundAmount,
        },
        status: InvoiceStatus.SENT, // Reset to SENT status after refund
        balanceDue: { increment: refundAmount },
      },
    });

    // Create history entry
    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: payment.invoiceId,
        action: HistoryAction.STATUS_CHANGED,
        description: `Payment refunded: ${refundAmount}`,
        performedBy: userId,
      },
    });

    try {
      this.notifications.emit(userId, 'payment.refunded', {
        paymentId: id,
        invoiceId: payment.invoiceId,
        amount: refundAmount,
      });
    } catch {}
    return {
      refund,
      message: 'Payment refunded successfully',
    };
  }

  async getPaymentStatistics(userId: string) {
    const currentMonth = new Date();
    currentMonth.setDate(1);
    currentMonth.setHours(0, 0, 0, 0);

    const lastMonth = new Date(currentMonth);
    lastMonth.setMonth(lastMonth.getMonth() - 1);

    const [totalReceived, monthlyReceived, pendingPayments, failedPayments] =
      await Promise.all([
        // Total received
        this.prisma.payment.aggregate({
          where: {
            invoice: { userId },
            status: PaymentStatus.COMPLETED,
            amount: { gt: 0 },
          },
          _sum: { amount: true },
        }),
        // This month received
        this.prisma.payment.aggregate({
          where: {
            invoice: { userId },
            status: PaymentStatus.COMPLETED,
            amount: { gt: 0 },
            paymentDate: { gte: currentMonth },
          },
          _sum: { amount: true },
        }),
        // Pending payments count
        this.prisma.payment.count({
          where: {
            invoice: { userId },
            status: PaymentStatus.PENDING,
          },
        }),
        // Failed payments count
        this.prisma.payment.count({
          where: {
            invoice: { userId },
            status: PaymentStatus.FAILED,
          },
        }),
      ]);

    return {
      totalReceived: totalReceived._sum.amount || 0,
      monthlyReceived: monthlyReceived._sum.amount || 0,
      pendingPayments,
      failedPayments,
    };
  }
}

import { Body, Controller, Param, Post } from '@nestjs/common';
import { PaymentsService } from './payments.service';
import { InvoicesService } from '../invoices/invoices.service';
import { StripeService } from './stripe.service';

// Public endpoints to allow payments by shareId (no auth)
// Routes:
// POST /public/invoices/:shareId/payments/record
// POST /public/invoices/:shareId/payments/process

@Controller('public/invoices')
export class PublicPaymentsController {
  constructor(
    private readonly paymentsService: PaymentsService,
    private readonly invoicesService: InvoicesService,
    private readonly stripeService: StripeService,
  ) {}

  @Post(':shareId/payments/record')
  async recordPublic(
    @Param('shareId') shareId: string,
    @Body()
    body: {
      amount: number;
      paymentMethod:
        | 'CASH'
        | 'BANK_TRANSFER'
        | 'CREDIT_CARD'
        | 'DEBIT_CARD'
        | 'PAYPAL'
        | 'STRIPE'
        | 'CHECK'
        | 'OTHER';
      paymentDate?: string | Date;
      transactionId?: string;
      notes?: string;
    },
  ) {
    // Resolve invoice by shareId, then delegate to service with userId + invoiceId
    const invoice =
      await this.invoicesService.getPublicInvoiceByShareId(shareId);
    const payload = {
      invoiceId: invoice.id,
      amount: Number(body.amount),
      paymentMethod: body.paymentMethod,
      paymentDate: body.paymentDate ? new Date(body.paymentDate) : undefined,
      transactionId: body.transactionId,
      notes: body.notes,
    } as any;
    return this.paymentsService.create(invoice.userId, payload);
  }

  @Post(':shareId/payments/process')
  async processPublic(
    @Param('shareId') shareId: string,
    @Body()
    body: {
      amount: number;
      paymentMethod:
        | 'CREDIT_CARD'
        | 'BANK_TRANSFER'
        | 'PAYPAL'
        | 'STRIPE'
        | 'CASH'
        | 'DEBIT_CARD'
        | 'CHECK'
        | 'OTHER';
      cardNumber?: string;
      cardExpiry?: string;
      cardCvv?: string;
      bankAccount?: string;
      notes?: string;
    },
  ) {
    const invoice =
      await this.invoicesService.getPublicInvoiceByShareId(shareId);
    const payload = {
      invoiceId: invoice.id,
      amount: Number(body.amount),
      paymentMethod: body.paymentMethod,
      cardNumber: body.cardNumber,
      cardExpiry: body.cardExpiry,
      cardCvv: body.cardCvv,
      bankAccount: body.bankAccount,
      notes: body.notes,
    } as any;
    return this.paymentsService.processPayment(invoice.userId, payload);
  }

  // Stripe Checkout (public)  returns a URL to redirect the client for card payment
  @Post(':shareId/payments/stripe/checkout')
  async createStripeCheckout(@Param('shareId') shareId: string) {
    return this.stripeService.createInvoiceCheckoutByShareId(shareId);
  }
}

import {
  Body,
  Controller,
  Get,
  Headers,
  Post,
  Req,
  UseGuards,
} from '@nestjs/common';
import type { Request } from 'express';
import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
import { EmailVerifiedGuard } from '../auth/guards/email-verified.guard';
import { CurrentUser } from '../../common/decorators/current-user.decorator';
import { StripeService } from './stripe.service';

@Controller()
export class StripeController {
  constructor(private readonly stripe: StripeService) {}

  // Admin SaaS subscription checkout (for current authenticated user)
  @Post('payments/stripe/subscription/checkout')
  @UseGuards(JwtAuthGuard, EmailVerifiedGuard)
  createSubscriptionCheckout(
    @CurrentUser() user: any,
    @Body() body: { plan?: 'BASIC' | 'PREMIUM' },
  ) {
    const plan = body?.plan === 'BASIC' ? 'BASIC' : 'PREMIUM';
    return this.stripe.createSubscriptionCheckoutSession(user.id, plan);
  }

  @Post('payments/stripe/subscription/portal')
  @UseGuards(JwtAuthGuard, EmailVerifiedGuard)
  createPortal(@CurrentUser() user: any) {
    return this.stripe.createBillingPortalSession(user.id);
  }

  // User payments: Stripe Connect onboarding/status
  @Post('payments/stripe/connect/onboarding')
  @UseGuards(JwtAuthGuard, EmailVerifiedGuard)
  createConnectOnboarding(@CurrentUser() user: any) {
    return this.stripe.createConnectOnboardingLink(user.id);
  }

  @Get('payments/stripe/connect/status')
  @UseGuards(JwtAuthGuard, EmailVerifiedGuard)
  getConnectStatus(@CurrentUser() user: any) {
    return this.stripe.getConnectStatus(user.id);
  }

  // Webhook endpoint (no auth)
  @Post('webhooks/stripe')
  async webhook(
    @Req() req: Request,
    @Headers('stripe-signature') signature?: string,
  ) {
    // req['rawBody'] is available because app.enable rawBody in NestFactory.create({ rawBody: true })
    const raw = (req as any).rawBody as Buffer;
    return this.stripe.handleWebhook(raw, signature);
  }
}

import {
  Injectable,
  BadRequestException,
  NotFoundException,
} from '@nestjs/common';
import Stripe from 'stripe';
import { PrismaService } from '../../prisma/prisma.service';
import { Plan } from '../../common/enums';
import { EmailService } from '../email/email.service';

// Utility to read a set of keys from SystemSettings
async function readSettings(prisma: PrismaService, keys: string[]) {
  const rows = await prisma.systemSettings.findMany({
    where: { key: { in: keys } },
  });
  const map = new Map(rows.map((r) => [r.key, r.value])) as Map<string, any>;
  return map;
}

@Injectable()
export class StripeService {
  constructor(
    private prisma: PrismaService,
    private emailService: EmailService,
  ) {}

  private async getStripe(): Promise<Stripe> {
    const map = await readSettings(this.prisma, ['STRIPE_SECRET_KEY']);
    const sk = String(map.get('STRIPE_SECRET_KEY') || '');
    if (!sk || sk === '__SECRET__') {
      throw new BadRequestException(
        'Stripe is not configured (missing STRIPE_SECRET_KEY)',
      );
    }
    return new Stripe(sk, { apiVersion: '2024-11-20.acacia' as any });
  }

  // --- Admin SaaS subscription billing ---
  private async getPrices(): Promise<{ BASIC?: string; PREMIUM?: string }> {
    const map = await readSettings(this.prisma, [
      'STRIPE_PRICE_BASIC',
      'STRIPE_PRICE_PREMIUM',
    ]);
    return {
      BASIC: map.get('STRIPE_PRICE_BASIC')
        ? String(map.get('STRIPE_PRICE_BASIC'))
        : undefined,
      PREMIUM: map.get('STRIPE_PRICE_PREMIUM')
        ? String(map.get('STRIPE_PRICE_PREMIUM'))
        : undefined,
    };
  }

  private async getFrontendUrl(): Promise<string> {
    // Prefer configured FRONTEND_ORIGINS first value, else default to localhost:3000
    const origins = (process.env.FRONTEND_ORIGINS || '')
      .split(',')
      .map((s) => s.trim())
      .filter(Boolean);
    return origins[0] || 'http://localhost:3000';
  }

  private async ensureStripeCustomerId(userId: string): Promise<string> {
    const user = await this.prisma.user.findUnique({ where: { id: userId } });
    if (!user) throw new NotFoundException('User not found');
    if ((user as any).stripeCustomerId) return (user as any).stripeCustomerId;
    const stripe = await this.getStripe();
    const customer = await stripe.customers.create({
      email: user.email,
      name:
        `${user.firstName || ''} ${user.lastName || ''}`.trim() || undefined,
    });
    const prismaAny = this.prisma as any;
    await prismaAny.user.update({
      where: { id: userId },
      data: { stripeCustomerId: customer.id },
    });
    return customer.id;
  }

  async createSubscriptionCheckoutSession(
    userId: string,
    plan: 'BASIC' | 'PREMIUM',
  ) {
    const stripe = await this.getStripe();
    const prices = await this.getPrices();
    const priceId = plan === 'BASIC' ? prices.BASIC : prices.PREMIUM;
    if (!priceId)
      throw new BadRequestException(
        `Stripe price for ${plan} is not configured`,
      );
    const customer = await this.ensureStripeCustomerId(userId);
    const origin = await this.getFrontendUrl();
    const session = await stripe.checkout.sessions.create({
      mode: 'subscription',
      customer,
      line_items: [{ price: priceId, quantity: 1 }],
      success_url: `${origin}/settings?tab=billing&success=1`,
      cancel_url: `${origin}/settings?tab=billing&canceled=1`,
      metadata: { type: 'subscription', userId, plan },
    });
    return { url: session.url };
  }

  async createBillingPortalSession(userId: string) {
    const stripe = await this.getStripe();
    const customer = await this.ensureStripeCustomerId(userId);
    const origin = await this.getFrontendUrl();
    const portal = await stripe.billingPortal.sessions.create({
      customer,
      return_url: `${origin}/settings?tab=billing`,
    });
    return { url: portal.url };
  }

  // --- Stripe Connect for user invoice payments ---
  async createOrGetConnectAccount(userId: string) {
    const stripe = await this.getStripe();
    const user = await this.prisma.user.findUnique({ where: { id: userId } });
    if (!user) throw new NotFoundException('User not found');
    if ((user as any).stripeConnectAccountId)
      return (user as any).stripeConnectAccountId;
    const account = await stripe.accounts.create({
      type: 'express',
      country: 'US',
      email: user.companyEmail || user.email,
      business_type: 'individual',
      metadata: { userId },
    });
    const prismaAny = this.prisma as any;
    await prismaAny.user.update({
      where: { id: userId },
      data: { stripeConnectAccountId: account.id },
    });
    return account.id;
  }

  async createConnectOnboardingLink(userId: string) {
    const stripe = await this.getStripe();
    const accountId = await this.createOrGetConnectAccount(userId);
    const origin = await this.getFrontendUrl();
    const link = await stripe.accountLinks.create({
      account: accountId,
      refresh_url: `${origin}/settings?tab=billing&connect=refresh`,
      return_url: `${origin}/settings?tab=billing&connect=return`,
      type: 'account_onboarding',
    });
    return { url: link.url };
  }

  async getConnectStatus(userId: string) {
    const stripe = await this.getStripe();
    const user = await this.prisma.user.findUnique({ where: { id: userId } });
    if (!user) throw new NotFoundException('User not found');
    if (!(user as any).stripeConnectAccountId) return { connected: false };
    const acct = await stripe.accounts.retrieve(
      (user as any).stripeConnectAccountId,
    );
    const detailsSubmitted = (acct as any)?.details_submitted || false;
    const chargesEnabled = (acct as any)?.charges_enabled || false;
    return {
      connected: !!(user as any).stripeConnectAccountId,
      detailsSubmitted,
      chargesEnabled,
    };
  }

  // Create a Checkout session for paying an invoice by shareId using destination charges to a user's connected account
  async createInvoiceCheckoutByShareId(shareId: string) {
    const invoice = await this.prisma.invoice.findFirst({
      where: { shareId, shareEnabled: true },
      include: { user: true, client: true },
    });
    if (!invoice) throw new NotFoundException('Invoice not found');
    const amountDue = Math.max(
      0,
      Math.round((invoice.balanceDue || invoice.totalAmount) * 100),
    ); // in cents
    if (amountDue <= 0)
      throw new BadRequestException('Invoice has no balance due');

    const currency = (invoice.currency || 'USD').toLowerCase();
    const stripe = await this.getStripe();

    const platformFeeBpsRow = await this.prisma.systemSettings.findUnique({
      where: { key: 'STRIPE_PLATFORM_FEE_BPS' },
    });
    const feeBps = Number(platformFeeBpsRow?.value || 0);
    const application_fee_amount = Math.floor(
      (amountDue * Math.max(0, Math.min(10000, feeBps))) / 10000,
    );

    // Enforce that the invoice owner's Stripe Connect account is set up
    const destination = (invoice.user as any).stripeConnectAccountId;
    if (!destination) {
      throw new BadRequestException(
        'Payment is not available yet. The seller has not connected Stripe. Please contact them to set up payments.',
      );
    }
    // Verify charges are enabled on the connected account
    try {
      const acct = await stripe.accounts.retrieve(destination);
      const chargesEnabled = (acct as any)?.charges_enabled;
      if (!chargesEnabled) {
        throw new BadRequestException(
          'Payment is not available yet. The sellers Stripe account is not fully enabled for charges.',
        );
      }
    } catch (e) {
      if (e instanceof BadRequestException) throw e;
      throw new BadRequestException(
        'Unable to verify sellers Stripe account. Please try again later.',
      );
    }

    const origin = await this.getFrontendUrl();
    const session = await stripe.checkout.sessions.create({
      mode: 'payment',
      line_items: [
        {
          price_data: {
            currency,
            product_data: {
              name: `Invoice ${invoice.invoiceNumber}`,
              description: invoice.client?.name
                ? `Client: ${invoice.client.name}`
                : undefined,
            },
            unit_amount: amountDue,
          },
          quantity: 1,
        },
      ],
      customer_email: invoice.client?.email || undefined,
      success_url: `${origin}/public/invoices/${encodeURIComponent(shareId)}?paid=1`,
      cancel_url: `${origin}/public/invoices/${encodeURIComponent(shareId)}?canceled=1`,
      metadata: {
        type: 'invoice_payment',
        invoiceId: invoice.id,
        userId: invoice.userId,
        shareId,
      },
      payment_intent_data: {
        transfer_data: { destination },
        application_fee_amount,
      },
    });

    return { url: session.url };
  }

  // --- Webhook handling ---
  async handleWebhook(rawBody: Buffer, signature: string | undefined) {
    const map = await readSettings(this.prisma, [
      'STRIPE_SECRET_KEY',
      'STRIPE_WEBHOOK_SECRET',
      'STRIPE_PRICE_BASIC',
      'STRIPE_PRICE_PREMIUM',
    ]);
    const sk = String(map.get('STRIPE_SECRET_KEY') || '');
    const wh = String(map.get('STRIPE_WEBHOOK_SECRET') || '');
    if (!sk || !wh) throw new BadRequestException('Stripe is not configured');
    const stripe = new Stripe(sk, { apiVersion: '2024-11-20.acacia' as any });

    let event: Stripe.Event;
    try {
      event = stripe.webhooks.constructEvent(rawBody, signature || '', wh);
    } catch (err: any) {
      throw new BadRequestException(
        `Webhook signature verification failed: ${err?.message || err}`,
      );
    }

    switch (event.type) {
      case 'checkout.session.completed': {
        const session = event.data.object;
        const type = (session.metadata as any)?.type;
        if (type === 'invoice_payment') {
          await this.onInvoicePaymentSessionCompleted(session);
        } else if (type === 'subscription') {
          await this.onSubscriptionSessionCompleted(session);
        }
        break;
      }
      case 'customer.subscription.deleted': {
        const sub = event.data.object;
        const customerId =
          typeof sub.customer === 'string' ? sub.customer : sub.customer?.id;
        if (customerId) await this.onSubscriptionCanceled(customerId);
        break;
      }
      default:
        // noop
        break;
    }

    return { received: true };
  }

  private async onInvoicePaymentSessionCompleted(
    session: Stripe.Checkout.Session,
  ) {
    const invoiceId = (session.metadata as any)?.invoiceId;
    const userId = (session.metadata as any)?.userId;
    if (!invoiceId || !userId) return;
    const invoice = await this.prisma.invoice.findUnique({
      where: { id: invoiceId },
      include: { payments: true, client: true, user: true },
    });
    if (!invoice) return;

    const paidCents = session.amount_total || 0;
    const paid = Math.max(0, paidCents / 100);

    // Create a payment record
    await this.prisma.payment.create({
      data: {
        invoiceId: invoice.id,
        amount: paid,
        paymentMethod: 'STRIPE' as any,
        paymentDate: new Date(),
        transactionId: String(session.payment_intent || session.id),
        status: 'COMPLETED' as any,
        notes: 'Stripe Checkout payment',
        paymentNumber: `PMT-${Date.now()}`,
        netAmount: paid,
      },
    });

    // Update invoice totals and status
    const newTotalPaid =
      (invoice.payments || [])
        .filter((p) => p.status === 'COMPLETED')
        .reduce((s, p) => s + p.amount, 0) + paid;

    const fullyPaid = newTotalPaid >= invoice.totalAmount - 0.0001;
    await this.prisma.invoice.update({
      where: { id: invoice.id },
      data: {
        status: fullyPaid ? ('PAID' as any) : ('PARTIALLY_PAID' as any),
        paidAmount: newTotalPaid,
        balanceDue: Math.max(0, invoice.totalAmount - newTotalPaid),
        paidAt: fullyPaid ? new Date() : invoice.paidAt,
      },
    });

    await this.prisma.invoiceHistory.create({
      data: {
        invoiceId: invoice.id,
        action: 'PAYMENT_RECEIVED' as any,
        description: `Stripe payment of ${paid.toFixed(2)} received via Checkout`,
        performedBy: userId,
      },
    });

    // Send emails (respect preferences)
    try {
      if (
        invoice.user?.emailNotificationsEnabled &&
        invoice.user?.emailNotifyPaymentReceived
      ) {
        await this.emailService.sendPaymentReceivedEmail(
          invoice.user as any,
          invoice.client as any,
          invoice as any,
          paid,
        );
      }
      if (invoice.user?.emailNotificationsEnabled) {
        const updatedBalance = Math.max(
          0,
          (invoice.totalAmount || 0) - ((invoice.paidAmount || 0) + paid),
        );
        await this.emailService.sendClientPaymentReceiptEmail(
          invoice.client as any,
          {
            ...invoice,
            paidAmount: (invoice.paidAmount || 0) + paid,
            balanceDue: updatedBalance,
          } as any,
          paid,
          invoice.user as any,
        );
      }
    } catch (e) {
      console.error(
        'Failed to send payment email after Stripe payment:',
        e?.message || e,
      );
    }
  }

  private async onSubscriptionSessionCompleted(
    session: Stripe.Checkout.Session,
  ) {
    const userId = (session.metadata as any)?.userId;
    const plan = (session.metadata as any)?.plan as 'BASIC' | 'PREMIUM';
    if (!userId || !plan) return;

    // Retrieve the subscription to get current period end
    const stripe = await this.getStripe();
    const subscriptionId =
      typeof session.subscription === 'string'
        ? session.subscription
        : (session.subscription as any)?.id;
    let periodEnd: Date | null = null;
    if (subscriptionId) {
      const sub = await stripe.subscriptions.retrieve(subscriptionId);
      const endTs = sub.current_period_end || 0;
      periodEnd = endTs ? new Date(endTs * 1000) : null;
    }

    const invoiceLimit = plan === 'BASIC' ? 50 : 0;
    await this.prisma.user.update({
      where: { id: userId },
      data: {
        subscriptionPlan: plan as any,
        subscriptionEnd: periodEnd,
        invoiceLimit,
      },
    });
  }

  private async onSubscriptionCanceled(customerId: string) {
    const prismaAny = this.prisma as any;
    const user = await prismaAny.user.findFirst({
      where: { stripeCustomerId: customerId },
    });
    if (!user) return;
    await prismaAny.user.update({
      where: { id: user.id },
      data: {
        subscriptionPlan: 'FREE' as any,
        subscriptionEnd: null,
        invoiceLimit: 5,
      },
    });
  }
}

