    @Body() updateSettingsDto: UpdateSettingsDto,
  ) {
    return this.usersService.updateSettings(user.id, updateSettingsDto);
  }

  @Put('password')
  async changePassword(
    @CurrentUser() user,
    @Body() changePasswordDto: ChangePasswordDto,
  ) {
    return this.usersService.changePassword(user.id, changePasswordDto);
  }

  @Delete('account')
  async deleteAccount(@CurrentUser() user) {
    return this.usersService.deleteAccount(user.id);
  }

  @Post('upload-logo')
  @UseInterceptors(
    FileInterceptor('file', {
      storage: diskStorage({
        destination: (_req, _file, cb) => {
          const dir = join(process.cwd(), 'uploads', 'logos');
          try {
            fs.mkdirSync(dir, { recursive: true });
          } catch {}
          cb(null, dir);
        },
        filename: (_req, file, cb) => {
          const unique = Date.now() + '-' + Math.round(Math.random() * 1e9);
          const extension = extname(file.originalname) || '';
          cb(null, `logo-${unique}${extension}`);
        },
      }),
    }),
  )
  async uploadLogo(
    @CurrentUser() user,
    @UploadedFile() file: Express.Multer.File,
  ) {
    if (!file) {
      return { message: 'No file uploaded' };
    }
    // File saved by Multer to /uploads/logos with generated filename
    const logoUrl = `/uploads/logos/${file.filename}`;
    return this.usersService.uploadLogo(user.id, logoUrl);
  }

  // Mock billing endpoints (no real payment integration)
  @Post('upgrade-mock')
  async upgradeMock(
    @CurrentUser() user,
    @Body() body: { plan?: 'BASIC' | 'PREMIUM' },
  ) {
    const plan = body?.plan === 'BASIC' ? 'BASIC' : 'PREMIUM';
    return this.usersService.upgradePlanMock(user.id, plan);
  }

  @Post('downgrade-mock')
  async downgradeMock(@CurrentUser() user) {
    return this.usersService.downgradeToFreeMock(user.id);
  }
}

import {
  Injectable,
  NotFoundException,
  BadRequestException,
} from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import {
  UpdateProfileDto,
  UpdateSettingsDto,
  ChangePasswordDto,
} from './dto/update-user.dto';
import * as bcrypt from 'bcryptjs';
import { Plan } from '../../common/enums';
import { EmailService } from '../email/email.service';

@Injectable()
export class UsersService {
  constructor(
    private prisma: PrismaService,
    private emailService: EmailService,
  ) {}

  async getProfile(userId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
      select: {
        id: true,
        email: true,
        firstName: true,
        lastName: true,
        companyName: true,
        companyLogo: true,
        companyAddress: true,
        companyPhone: true,
        companyEmail: true,
        taxNumber: true,
        role: true,
        emailVerified: true,
        createdAt: true,
        updatedAt: true,
      },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    return user;
  }

  async updateProfile(userId: string, updateProfileDto: UpdateProfileDto) {
    const user = await this.prisma.user.update({
      where: { id: userId },
      data: updateProfileDto,
      select: {
        id: true,
        email: true,
        firstName: true,
        lastName: true,
        companyName: true,
        companyLogo: true,
        companyAddress: true,
        companyPhone: true,
        companyEmail: true,
        taxNumber: true,
      },
    });

    return user;
  }

  async getSettings(userId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
      select: {
        invoicePrefix: true,
        invoiceStartNumber: true,
        currency: true,
        taxRate: true,
        paymentTerms: true,
        invoiceNotes: true,
        invoiceFooter: true,
        // Notification preferences
        emailNotificationsEnabled: true,
        emailNotifyNewInvoice: true,
        emailNotifyPaymentReceived: true,
        emailNotifyInvoiceOverdue: true,
        emailNotifyWeeklySummary: true,
        emailNotifyNewClientAdded: true,
        subscriptionPlan: true,
        subscriptionEnd: true,
        invoiceLimit: true,
      },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    return user;
  }

  async updateSettings(userId: string, updateSettingsDto: UpdateSettingsDto) {
    const user = await this.prisma.user.update({
      where: { id: userId },
      data: updateSettingsDto,
      select: {
        invoicePrefix: true,
        invoiceStartNumber: true,
        currency: true,
        taxRate: true,
        paymentTerms: true,
        invoiceNotes: true,
        invoiceFooter: true,
        // Notification preferences
        emailNotificationsEnabled: true,
        emailNotifyNewInvoice: true,
        emailNotifyPaymentReceived: true,
        emailNotifyInvoiceOverdue: true,
        emailNotifyWeeklySummary: true,
        emailNotifyNewClientAdded: true,
      },
    });

    return user;
  }

  async changePassword(userId: string, changePasswordDto: ChangePasswordDto) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new NotFoundException('User not found');
    }

    const isPasswordValid = await bcrypt.compare(
      changePasswordDto.currentPassword,
      user.password,
    );

    if (!isPasswordValid) {
      throw new BadRequestException('Current password is incorrect');
    }

    const hashedPassword = await bcrypt.hash(changePasswordDto.newPassword, 10);

    await this.prisma.user.update({
      where: { id: userId },
      data: { password: hashedPassword },
    });

    return { message: 'Password changed successfully' };
  }

  async deleteAccount(userId: string) {
    await this.prisma.user.delete({
      where: { id: userId },
    });

    return { message: 'Account deleted successfully' };
  }

  async uploadLogo(userId: string, logoUrl: string) {
    const user = await this.prisma.user.update({
      where: { id: userId },
      data: { companyLogo: logoUrl },
      select: {
        companyLogo: true,
      },
    });

    return user;
  }

  // Mock billing operations
  async upgradePlanMock(userId: string, plan: 'BASIC' | 'PREMIUM' = 'PREMIUM') {
    const targetPlan = plan === 'BASIC' ? Plan.BASIC : Plan.PREMIUM;
    // 30 days from now
    const subscriptionEnd = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000);
    const invoiceLimit = targetPlan === Plan.BASIC ? 50 : 0; // 0 means unlimited in enforcement checks

    const updated = await this.prisma.user.update({
      where: { id: userId },
      data: {
        subscriptionPlan: targetPlan as any,
        subscriptionEnd,
        invoiceLimit,
      },
      select: {
        subscriptionPlan: true,
        subscriptionEnd: true,
        invoiceLimit: true,
        emailNotificationsEnabled: true,
        email: true,
        firstName: true,
        companyName: true,
      },
    });

    // Fire-and-forget email notification
    if (updated?.emailNotificationsEnabled) {
      void (async () => {
        try {
          await this.emailService.sendPlanUpgradedEmail(
            {
              email: updated.email,
              firstName: updated.firstName,
              companyName: updated.companyName,
            } as any,
            String(updated.subscriptionPlan),
            updated.subscriptionEnd,
            updated.invoiceLimit,
          );
        } catch (e) {
          console.error('Failed to send plan upgraded email:', e?.message || e);
        }
      })();
    }

    return {
      subscriptionPlan: updated.subscriptionPlan,
      subscriptionEnd: updated.subscriptionEnd,
      invoiceLimit: updated.invoiceLimit,
      message: `Upgraded to ${targetPlan} (mock)`,
    } as const;
  }

  async downgradeToFreeMock(userId: string) {
    const updated = await this.prisma.user.update({
      where: { id: userId },
      data: {
        subscriptionPlan: Plan.FREE as any,
        subscriptionEnd: null,
        invoiceLimit: 5,
      },
      select: {
        subscriptionPlan: true,
        subscriptionEnd: true,
        invoiceLimit: true,
        emailNotificationsEnabled: true,
        email: true,
        firstName: true,
        companyName: true,
      },
    });

    if (updated?.emailNotificationsEnabled) {
      void (async () => {
        try {
          await this.emailService.sendPlanDowngradedEmail(
            {
              email: updated.email,
              firstName: updated.firstName,
              companyName: updated.companyName,
            } as any,
            String(updated.subscriptionPlan),
          );
        } catch (e) {
          console.error(
            'Failed to send plan downgraded email:',
            e?.message || e,
          );
        }
      })();
    }

    return {
      subscriptionPlan: updated.subscriptionPlan,
      subscriptionEnd: updated.subscriptionEnd,
      invoiceLimit: updated.invoiceLimit,
      message: 'Downgraded to FREE (mock)',
    } as const;
  }
}

